    const [avgApprState, setAvgAppreciationState] = useState(0);
    const [medianApprState, setMedianAppreciationState] = useState(0);
    const [minApprState, setMinAppreciationState] = useState(0);
    const [maxApprState, setMaxAppreciationState] = useState(0);

    const [numCyclesState, setNumCyclesState] = useState(0);
    const [numFailsState, setNumFailsState] = useState(0);
    const [minFailAgeState, setMinFailAgeState] = useState(0);
    const [medianFailAgeState, setMedianFailAgeState] = useState(0);

    const svgID = 'd3target';
    const perRunClass = 'perrundata';
    const hoverLineID = 'hoverLine';
    const ttWrapID = 'ttwrapper';
    const ttBackID = 'ttbackground';
    const ttAgeID = 'ttage';
    const margin = { top: 40, right: 80, bottom: 60, left: 100 };
    const totalWidth = 960;
    const totalHeight = 700;
    const boundedWidth = totalWidth - margin.left - margin.right;
    const boundedHeight = totalHeight - margin.top - margin.bottom;
    const tooltipWidth = 75;
    const tooltipHeight = 75;
    const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
    const plotTranslate = "translate(" + margin.left + "," + totalHeight + ")";
    const portfolioLineColor = "Blue";
    const portfolioFailedLineColor = "OrangeRed";
    const normalStrokeWidth = 1.5;
    var allCycleMeta = [];

    const getXScale = () => { 
        var xExt = [props.currentage, props.lifeexpectancy];
        return d3.scaleLinear()
                    .domain(xExt)
                    .range([ 0, boundedWidth ]);
    }

    const getYScale = (rangeMin, rangeMax) => {
        var yExt = [rangeMin, rangeMax];
        return d3.scaleLinear()
                    .domain(yExt)
                    .range([ boundedHeight, 0 ]);
    }

    const drawAxes = (svg, xScaleIn, yScaleIn, rangeMin, rangeMax) => {

        svg.append("g")
            .attr("class", perRunClass)
            .attr("transform", "translate(0," + boundedHeight + ")")
            .call(d3.axisBottom(xScaleIn));
        svg.append("g")
            .attr("class", perRunClass)
            .call(d3.axisLeft(yScaleIn));

        // draw line at zero y
        var yScale = getYScale(rangeMin, rangeMax);
        const zeroY = yScale(0.0);

        svg.append("g")
           .attr("class", perRunClass)
           .append("rect")
           .attr("pointer-events", "none")
           .attr("stroke-width", "2.5px")
           .attr("stroke", "DarkRed")
           .attr('x', 0.0)
           .attr('y', zeroY)
           .attr("height", ".25px")
           .attr("width", boundedWidth);
    };

    const drawPortfolioLine = (svg, xScaleIn, yScaleIn, oneCycleMeta, oneCycle) => {
        // Add the equity line
        svg.append("path")
            .datum(oneCycle)
            .attr('class', perRunClass)
            .attr("fill", "none")
            .attr("pointer-events", "none")
            .style("opacity", 1)
            .attr("stroke", oneCycleMeta.lineColor)
            .attr("stroke-width", normalStrokeWidth)
            .attr("d", d3.line()
                        .x(function(d) { return xScaleIn(d.age) })
                        .y(function(d) { return yScaleIn(d.endValue) })
            );        
    }

    const prepHoverStuff = (svg) => {
        const tooltipWrapper = svg
                .append('g')
                .attr('id', ttWrapID)
                .attr('display', 'none');

        const tooltipBackground = tooltipWrapper.append('rect')
                        .style('opacity', 0.70)
                        .attr('id', ttBackID)
                        .attr('width', tooltipWidth)
                        .attr('height', tooltipHeight)
                        .attr("pointer-events", "none")
                        .attr("fill", "#e8e8e8"); 

        const tooltipText = tooltipWrapper.append('g').append('text');

        tooltipText.attr("pointer-events", "none")
                    .attr('font-weight', 900)
                    .attr('text-anchor', 'left');

        const ageTT = tooltipText
                .append('tspan')
                .attr('id', ttAgeID)
                .attr('x', '5')
                .attr('y', '5')
                .attr('dy', '15px')
                .attr("pointer-events", "none");

        const hoverLine = svg
                .append("g")
                .append("rect")
                    .style('opacity', 0)
                    .attr('id', hoverLineID)
                    .attr("pointer-events", "none")
                    .attr("class", "dotted")
                    .attr("stroke-width", "1px")
                    .attr("width", ".5px")
                    .attr("height", boundedHeight);

    }

    const getHoverLine = () => {
        return d3.select('#' + hoverLineID);
    }

    const getTooltipWrapper = () => {
        return d3.select('#' + ttWrapID);
    }

    const getTooltipBackground = () => {
        return d3.select('#' + ttBackID);
    }

    const getTooltipAgeSpan = () => {
        return d3.select('#' + ttAgeID);
    }

    const handleMouseOver = (e) => {
        console.log('handleMouseOver');
        getHoverLine().style('opacity', 1);
        getTooltipWrapper().attr('display', null);
    }

    const handleMouseMove = (e) => {
        console.log('handleMouseMove');

        const bisect = d3.bisector((d) => d.age).left;
        var xScaleIn = getXScale();
        const coords = d3.pointer(e);
        const x0 = xScaleIn.invert(coords[0]);
        const oneCycleData = allCycleDataState[0];
        const i = bisect(oneCycleData, x0, 1);
        const selectedData = oneCycleData[i];
        const clientX = xScaleIn(selectedData.age);
        var tooltipX = clientX;

        // prevent the tooltip from getting clipped.
        const tooltipWidth = 75;             
        if (boundedWidth <= (clientX + tooltipWidth)) {
            tooltipX = clientX - tooltipWidth;
        }
        getTooltipAgeSpan().text('age: ' + selectedData.age);

        const ttBounds = getTooltipWrapper().node().getBBox();
        getTooltipBackground()                
            .attr('width', ttBounds.width)
            .attr('height', ttBounds.height);

        getTooltipWrapper().attr("transform", "translate(" + tooltipX + "," + coords[1] + ")");  

        getHoverLine().attr('x', clientX);
        getTooltipWrapper().attr('x', tooltipX)
    };

    const handleMouseLeave = () => {
        console.log('handleMouseLeave');
        getHoverLine().style('opacity', 0);
        getTooltipWrapper().attr('display', 'none');
    };

    const makeCurrency = (num) => {
        let dollarUSLocale = Intl.NumberFormat('en-US', {
            style: "currency",
            currency: "USD",
        });
        return dollarUSLocale.format(num);
    }

    const cleanupPrev = (svg) => {
        const perRunSelect = '.' + perRunClass;
        d3.selectAll(perRunSelect).remove();
    }

    const runCycle = (startIndex, numYears) => {
        var cycleData = [];

        for(var i = 0; i < numYears; i++){
            var obj = { "year": histData[startIndex + i].year,
                        "age": +(props.currentage) + i,
                        "beginValue": (i > 0) ? cycleData[i - 1].endValue : +(props.portfoliovalue),
                        "equityReturn": histData[startIndex + i].equity,
                        "bondReturn": histData[startIndex + i].bond,
                        "aggReturn": ((histData[startIndex + i].equity * (props.stockallocation / 100)) +
                                      (histData[startIndex + i].bond * (props.bondallocation / 100))),
                        "inflation": histData[startIndex + i].inflation,
                        "cumulativeCPI": (i > 0) ? 
                                         (cycleData[i - 1].cumulativeCPI + histData[startIndex + i].inflation) :
                                         (1 + histData[startIndex + i].inflation),
                        "spend": +(props.spendvalue),
                        "actualSpend": +(props.spendvalue),
                        "endValue": +(props.portfoliovalue),
                        "appr": 0,
                      };

            // summary : 
            //           translate beginValue to infl adj dollars
            var beginInflAdj = obj.beginValue * obj.cumulativeCPI;
            //           derive spend amount, and translate into infl adj dollars
            //           TODO - determine whether or not to vary ssincome with inflation
            var spendInflAdj = obj.spend * obj.cumulativeCPI;
            if (obj.age >= props.ssage) {
                spendInflAdj -= props.ssincome;
            }
            //           subtract adj spend and fees from adj begin
            var adjFees = (+(props.feepct) / 100) * beginInflAdj;
            var endAdjInfl = beginInflAdj - spendInflAdj - adjFees;
            //           calc gains, and add them
            var adjGains = endAdjInfl * obj.aggReturn;
            endAdjInfl += adjGains;
            //           translate results back to today's dollars
            obj.endValue = endAdjInfl / obj.cumulativeCPI;
            obj.actualSpend = spendInflAdj / obj.cumulativeCPI;
            obj.appr = adjGains / obj.cumulativeCPI;

            cycleData.push(obj);

            if (0 >= obj.endValue) {
                obj.endValue = 0;
                break;
            }
        }

        return cycleData;
    }

    const calcCycleMeta = (oneCycle, lifetime) => {
        var ext = d3.extent(oneCycle, (d) => d.endValue);
        var avg = d3.mean(oneCycle, (d) => d.endValue);
        var med = d3.median(oneCycle, (d) => d.endValue);
        var totalSpend = d3.sum(oneCycle, (d) => d.actualSpend);
        var totalAppr = d3.sum(oneCycle, (d) => d.appr);
        var avgReturns = d3.mean(oneCycle, (d) => d.aggReturn);
        var medReturns = d3.median(oneCycle, (d) => d.aggReturn);
        var extReturns = d3.extent(oneCycle, (d) => d.aggReturn);

        var oneMeta = {
            'startCycleValue': props.portfoliovalue,
            'endCycleValue': oneCycle[oneCycle.length - 1].endValue,
            'extent': ext,
            'mean': avg,
            'median': med,
            'totalSpend': totalSpend,
            'totalAppreciation': totalAppr,
            'meanReturn': avgReturns,
            'medianReturn': medReturns,
            'extReturns': extReturns,
            'fail': (oneCycle.length < lifetime),
            'failAge': (oneCycle.length < lifetime) ? 
                       (oneCycle.length + props.currentage) :
                       0,
            'lineColor': (oneCycle.length === lifetime) ? portfolioLineColor : portfolioFailedLineColor,
        };

        return oneMeta;
    }

    const showSummaryData = (allCycles) => {
        // TODO show mean/median actual spends, appr, and median/min/max returns
        var ext = d3.extent(allCycleMeta, (d) => d.endCycleValue);
        var avg = d3.mean(allCycleMeta, (d) => d.endCycleValue);
        var med = d3.median(allCycleMeta, (d) => d.endCycleValue);
        var avgSpend = d3.mean(allCycleMeta, (d) => d.totalSpend);        
        var medianSpend = d3.median(allCycleMeta, (d) => d.totalSpend);
        var extSpend = d3.extent(allCycleMeta, (d) => d.totalSpend);
        var avgReturns = d3.mean(allCycleMeta, (d) => d.totalReturn);
        var medianReturns = d3.median(allCycleMeta, (d) => d.totalReturn);
        var extReturns = d3.extent(allCycleMeta, (d) => d.totalReturn);
        var avgAppr = d3.mean(allCycleMeta, (d) => d.appr);
        var medianAppr = d3.median(allCycleMeta, (d) => d.appr);
        var extAppr = d3.extent(allCycleMeta, (d) => d.appr);
        var numFails = 0;
        var minFailAge = props.lifeexpectancy + 1;

        for (var i = 0; i < allCycleMeta.length; i++) {
            if (allCycleMeta[i].fail) {
                numFails++;
                minFailAge = Math.min(allCycleMeta[i].failAge, minFailAge);
            }
        }

        setAvgEndValueState(avg);
        setMedianEndValueState(med);
        setMinEndValueState(ext[0]);
        setMaxEndValueState(ext[1]);

        setAvgSpendState(avgSpend);
        setMedianSpendState(medianSpend);
        setMinSpendState(extSpend[0]);
        setMaxSpendState(extSpend[1]);

        setAvgReturnsState(avgReturns);
        setMedianReturnsState(medianReturns);
        setMinReturnsState(extReturns[0]);
        setMaxReturnsState(extReturns[1]);

        setAvgAppreciationState(avgAppr);
        setMedianAppreciationState(medianAppr);
        setMinAppreciationState(extAppr[0]);
        setMaxAppreciationState(extAppr[1]);

        setNumCyclesState(allCycleMeta.length);
        setNumFailsState(numFails);
        setMinFailAgeState(minFailAge);
    }

    const calcCycles = (svg) => {
        const lifetime = props.lifeexpectancy - props.currentage;
        const numCycles = (histData.length - lifetime);
        const startIndex = 0;
        var portMin = Number.POSITIVE_INFINITY;
        var portMax = Number.NEGATIVE_INFINITY;
        var allCycles = [];

        cleanupPrev();
        console.log('calcCycles');

        for (var i = 0; i < numCycles; i++) {
            var oneCycle = runCycle(startIndex + i, lifetime);
            var cycleMeta = calcCycleMeta(oneCycle, lifetime);

            allCycleMeta[i] = cycleMeta;
            allCycles[i] = oneCycle;

            portMin = Math.min(portMin, cycleMeta.extent[0]);
            portMax = Math.max(portMax, cycleMeta.extent[1]);
        }

        setAllCycleDataState(allCycles);
        showSummaryData(allCycles);

        const xScale = getXScale();
        const yScale = getYScale(portMin, portMax);

        drawAxes(svg, xScale, yScale, portMin, portMax);
        for (i = 0; i < numCycles; i++) {
            drawPortfolioLine(svg, xScale, yScale, allCycleMeta[i], allCycles[i]);
        }
    }

    React.useEffect(() => {

        const svgSelect = '#' + svgID;
        const svg = d3.select(svgSelect)
                      .append("g")
                      .attr("transform", marginTranslate);
        calcCycles(svg);

        prepHoverStuff(svg);
        console.log('effect');
    }, [props] );

    console.log('render');
    return (
        <div>
            <Stack direction="row">
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Summary
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {Number( (1 - (numFailsState / numCyclesState)) * 100).toFixed(2)}% success
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {numFailsState} failure of {numCyclesState} cycles                               
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            Minimum failure age : {minFailAgeState}
                        </Typography>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Portfolio End Value
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : {makeCurrency(medianEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : {makeCurrency(avgEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : {makeCurrency(maxEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : {makeCurrency(minEndValueState)}
                        </Typography>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Portfolio % Returns
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : {makeCurrency(medianReturnsState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : {makeCurrency(avgReturnsState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : {makeCurrency(maxReturnsState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : {makeCurrency(minReturnsState)}
                        </Typography>
                    </CardContent>
                </Card>                
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            $ Appreciation
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : {makeCurrency(medianApprState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : {makeCurrency(avgApprState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : {makeCurrency(maxApprState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : {makeCurrency(minApprState)}
                        </Typography>
                    </CardContent>
                </Card>                
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Total Spending
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : {makeCurrency(medianSpendState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : {makeCurrency(avgSpendState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : {makeCurrency(maxSpendState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : {makeCurrency(minSpendState)}
                        </Typography>
                    </CardContent>
                </Card>
            </Stack>
            <svg id={svgID} 
                    width={totalWidth}
                    height={totalHeight} 
            >
                <rect id='trackingRect'
                    style={{ opacity:0 }}
                    width={boundedWidth}
                    height={boundedHeight}
                    transform={marginTranslate}
                    fill='LightGrey'
                    onMouseEnter={handleMouseOver}
                    onMouseMove={handleMouseMove}
                    onMouseLeave={handleMouseLeave}
                />
            </svg>   
        </div>
    );
};

export default Chart;

