import * as React from "react";
import { useState } from "react";
import * as d3 from "d3";

function EndValueChart (props) {

    const [binMetadataState, setBinMetadataState] = useState([]);

    const svgID = 'endvaluechartsvg';
    const perRunClass = 'perrunevdata';
    const ttBinWrapID = 'binttwrapper';
    const ttBinBackID = 'binttback';
    const ttBinRangeID = 'binrange';
    const margin = { top: 40, right: 65, bottom: 40, left: 65 };
    const totalWidth = 960;
    const totalHeight = 300;
    const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
    const boundedWidth = totalWidth - margin.left - margin.right;
    const boundedHeight = totalHeight - margin.top - margin.bottom;
    var tooltipDiv = null;
    const ttDivID = 'ttdiv';
    const ttColor = 'white';
    const ttBackColor = 'black';
    const tooltipWidth = 75;
    const tooltipHeight = 75;
    const thresholdValues = [0, 0.25, 0.5, 1, 3, 5, 10, 50];

    const cleanupPrev = (svg) => {
        const perRunSelect = '.' + perRunClass;
        d3.selectAll(perRunSelect).remove();
    }

    const getXScaleForAxis = () => {
        const numThresholds = thresholdValues.length;
        var thresholdRange = [];
        var thresholdInc = boundedWidth / (numThresholds + 1);

        for (var i = 0; i <= thresholdValues.length; i++) {
            thresholdRange[i] = i * thresholdInc;
        }

        return d3.scaleThreshold()
                 .domain(thresholdValues)
                 .range(thresholdRange);
    }

    const calcBinSummary = (data) => {
        const oneBinSummary = {
            'binData': data,
            'extBin': d3.extent(data, (d) => d.adjEndCycleValue),
            'extPctStartValue': d3.extent(data, (d) => d.pctOfStart),
            'binCount': data.length,
        }

        return oneBinSummary;
    }

    const prepTooltip = (svg) => {
        // wrapper
        // background
        // text element
        //  tspan for each line
        //  x0, x1, % of start, % of results

        tooltipDiv = svg.append('div')
                        .attr('id', ttDivID)
                        .attr('color', ttColor)
                        .attr('background-color', ttBackColor)
                        .style("border-radius", "5px")
                        .style("padding", "10px")
                        .attr('text', 'yowsa');
        /*
        const tooltipWrapper = svg.append('g')
                                  .attr('id', ttBinWrapID)
                                  .attr('display', 'none');
        tooltipWrapper.append('rect')
                      .style('opacity', 0.70)
                      .attr('id', ttBinBackID)
                      .attr('width', tooltipWidth)
                      .attr('height', tooltipHeight)
                      .attr("pointer-events", "none")
                      .attr("fill", "#e8e8e8"); 

        const tooltipText = tooltipWrapper.append('g').append('text');
        tooltipText.attr("pointer-events", "none")
                   .attr('font-weight', 900)
                   .attr('text-anchor', 'left');          
        tooltipText.append('tspan')
                   .attr('id', ttBinRangeID)
                   .attr('x', '5')
                   .attr('y', '5')
                   .attr('dy', '15px')
                   .attr("pointer-events", "none");
                   */
}

    const drawHistogram = (svg) => {
        cleanupPrev(svg);

        const xScaleBins = getXScaleForAxis();
        const binFunc = d3.bin()
                          .value(function(d) { return d.pctOfStart; })   
                          .domain([thresholdValues[0], thresholdValues[thresholdValues.length - 1]])
                          .thresholds(thresholdValues); 
        const bins = binFunc(props.metadata);
        const yScaleBins = d3.scaleLinear()
                             .domain([0, d3.max(bins, function(d) { return d.length; })])
                             .range([boundedHeight, 0]);

        var binsMeta = [];
        for (var i = 0; i < bins.length; i++) {
            binsMeta[i] = calcBinSummary(bins[i]);
        }

        setBinMetadataState(binsMeta);

        svg.append("g")
            .attr("class", perRunClass)
            .attr("transform", "translate(0," + boundedHeight + ")")
            .call(d3.axisBottom(xScaleBins));
        svg.append("g")
           .attr("class", perRunClass)
           .call(d3.axisLeft(yScaleBins));

           // draw the histogram bars 
        var bars = svg.selectAll("rect")
           .data(bins)
           .enter()
           .append("rect")
                .attr("class", perRunClass)
                .attr("x", 1)
                .attr("transform", function(d) { return "translate(" + xScaleBins(d.x0) + "," + yScaleBins(d.length) + ")"; })
                .attr("width", function(d) { return xScaleBins(d.x1) - xScaleBins(d.x0) -1 ; })
                .attr("height", function(d) { return boundedHeight - yScaleBins(d.length); })
                .style("fill", "#69b3a2")
                .on('mousemove', function(d) { handleMouseMove(d); }.bind(binMetadataState));
    }

    const getTooltipWrapper = () => {
        return d3.select('#' + ttBinWrapID);
    }

    const getTooltipBackground = () => {
        return d3.select('#' + ttBinBackID);
    }

    const handleMouseOver = (e) => {
        getTooltipWrapper().attr('display', null);
    }

    const handleMouseMove = (e) => {
        console.log('move : ' + e.srcElement.__data__[0].adjEndCycleValue);
        const bisect = d3.bisector((d) => d.pctOfStart).left;
        var xScaleBin = getXScaleForAxis();
        const coords = d3.pointer(e);
        const x0 = xScaleBin.invertExtent(coords[0]);

        /*
        const oneCycleData = allCycleDataState[0];
        const i = bisect(oneCycleData, x0, 1);
        const selectedData = oneCycleData[i];
        const clientX = xScaleIn(selectedData.age);
        var tooltipX = clientX;

        // prevent the tooltip from getting clipped.
        const tooltipWidth = 75;             
        if (boundedWidth <= (clientX + tooltipWidth)) {
            tooltipX = clientX - tooltipWidth;
        }
        getTooltipAgeSpan().text('range: ' + selectedData.age);

        const ttBounds = getTooltipWrapper().node().getBBox();
        getTooltipBackground()                
            .attr('width', ttBounds.width)
            .attr('height', ttBounds.height);

        getTooltipWrapper().attr("transform", "translate(" + tooltipX + "," + coords[1] + ")");  
        getTooltipWrapper().attr('x', tooltipX);
        */
    };

    const handleMouseLeave = () => {
        getTooltipWrapper().attr('display', 'none');
    };

    React.useEffect(() => {

        const svgSelect = '#' + svgID;
        const svg = d3.select(svgSelect)
                      .append("g")
                      .attr("transform", marginTranslate);

        prepTooltip(svg);
        drawHistogram(svg);
        // console.log('endvaluechart effect');
    }, [props] );

    return (
        <div>
            <svg id={svgID}  
                width={totalWidth}
                height={totalHeight} 
             >
                <rect
                    style={{ opacity:0 }}
                    width={boundedWidth}
                    height={boundedHeight}
                    transform={marginTranslate}
                    fill='LightGrey'
                 >
                </rect>
            </svg>
        </div>
    );
};

export default EndValueChart;
