import * as React from "react";
import * as d3 from "d3";
import hist from "./data/hist-returns.csv";

// example : https://sharkcoder.com/data-visualization/d3-react
function HistoricalChart () {
    const [activeIndex, setActiveIndex] = React.useState(null);
    const [data, setData] = React.useState([]);

    const handleMouseMove = (e) => {
        const bisect = d3.bisector((d) => d.date).left,
            x0 = getX.invert(d3.pointer(e, this)[0]),
            index = bisect(data, x0, 1);
        setActiveIndex(index);
    };

    const handleMouseLeave = () => {
        setActiveIndex(null);
    };


    React.useEffect(() => {
        const margin = { top: 40, right: 80, bottom: 60, left: 50 },
        width = 960 - margin.left - margin.right,
        height = 280 - margin.top - margin.bottom,
        color = "OrangeRed";

        var svg = d3.select("#d3target")
                    .append("svg")
                    .attr("width", width + margin.left + margin.right)
                    .attr("height", height + margin.top + margin.bottom)
                    .append("g")
                    .attr("transform",
                        "translate(" + margin.left + "," + margin.top + ")");

        d3.csv(hist,function(d) {
            return { year: d.year, inflation: +d.inflation, equity: +d.equity, bond: +d.bond };
        })
        .then (function(data) {

            var pctFormat = d3.format("4.2%");
            var yearFormat = d3.format("d");

            var x = d3.scaleLinear()
                .domain([d3.min(data, function(d) { return d.year; }),
                        d3.max(data, function(d) { return d.year; })])
                .range([ 0, width ]);
            var xGen = d3.axisBottom(x)
                        .tickFormat(yearFormat);

            setData(data);

            svg.append("g")
                .attr("transform", "translate(0," + height + ")")
                .call(xGen);

            var y = d3.scaleLinear()
                .domain([d3.min(data, function(d) { return d.equity; }),
                            d3.max(data, function(d) { return d.equity; })])
                .range([ height, 0 ]);
            var yGen = d3.axisLeft(y)
                        .tickFormat(pctFormat);

            svg.append("g")
                .call(yGen);

            // Add the equity line
            svg.append("path")
            .datum(data)
            .attr("fill", "none")
            .attr("stroke", "steelblue")
            .attr("stroke-width", 1.5)
            .attr("d", d3.line()
                .x(function(d) { return x(d.year) })
                .y(function(d) { return y(d.equity) })
                );

            // Add the bonds line
            svg.append("path")
            .datum(data)
            .attr("fill", "none")
            .attr("stroke", "cyan")
            .attr("stroke-width", 1.5)
            .attr("d", d3.line()
                .x(function(d) { return x(d.year) })
                .y(function(d) { return y(d.bond) })
                );

            // Add the inflation line
            svg.append("path")
            .datum(data)
            .attr("fill", "none")
            .attr("stroke", "red")
            .attr("stroke-width", 1.5)
            .style("stroke-dasharray", ("5, 5"))
            .attr("d", d3.line()
                .x(function(d) { return x(d.year) })
                .y(function(d) { return y(d.inflation) })
                );   
        })

    }, []);

      return (
        <div>
             <div id="d3target" width="1200" height="1200" >

             </div> 
        </div>
      );
}

// d3 line chart example : https://www.d3-graph-gallery.com/graph/line_basic.html
