import * as React from "react";
import * as d3 from "d3";
import "./chartdata.css";
import hist from "./data/hist-returns.csv";

// d3 line chart example : https://www.d3-graph-gallery.com/graph/line_basic.html
// example to draw axis : https://wattenberger.com/blog/react-and-d3
// example : https://sharkcoder.com/data-visualization/d3-react

function HistoricalChart () {
    const [activeIndex, setActiveIndex] = React.useState(null);
    const [histData, setData] = React.useState([]);

    React.useEffect(() => {
        d3.csv(hist,function(d) {
            return { year: d.year, inflation: +d.inflation, equity: +d.equity, bond: +d.bond };
        })
        .then (function(dataset) {
            setData(dataset);
            d3.select("#d3target").datum(histData);
        })

        return () => undefined;
    }, []);

    const margin = { top: 40, right: 80, bottom: 60, left: 50 },
          width = 960 - margin.left - margin.right,
          height = 600 - margin.top - margin.bottom;
    const equityColor = "OrangeRed";

    const getX = d3
                 .scaleLinear()
                 .domain(d3.extent(histData, (d) => d.year))
                 .range([ 0, width ]);

    const getY = d3
                 .scaleLinear()
                 .domain(d3.extent(histData, (d) => d.equity))
                 .range([ height, 0 ]);

    const getXAxis = (ref) => {
        const xAxis = d3.axisBottom(getX);
        d3.select(ref).call(xAxis.tickFormat( d3.format("d") ));
    }

    const getYAxis = (ref) => {
        const yAxis = d3.axisLeft(getY);
        d3.select(ref).call(yAxis.tickFormat( d3.format("4.2%") ));
    }

    const equityPath = d3.line()
                         .x((d) => getX(d.date))
                         .y((d) => getY(d.equity))
                         .curve(d3.curveMonotoneX)(histData);

    const handleMouseMove = (e) => {
        const bisect = d3.bisector((d) => d.date).left,
            x0 = getX.invert(d3.pointer(e, this)[0]),
            index = bisect(histData, x0, 1);
        setActiveIndex(index);
    };

    const handleMouseLeave = () => {
        setActiveIndex(null);
    };

    return (
        <div>
            <svg id="d3target"
                viewBox={`0 0 ${width + margin.left + margin.right} 
                                ${height + margin.top + margin.bottom}`}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave} >

                <g className="axis xAxis"
                    ref={getXAxis}
                    transform={`translate(0,${height})`} />
                <g className="axis" ref={getYAxis} />                
                <path strokeWidth={3} stroke={equityColor} d={equityPath} />
            </svg>
        </div>
    );
}

export default HistoricalChart;
