import * as React from "react";
import { Box } from "@mui/system";
import { TextField } from "@mui/material";
import { List } from "@mui/material";
import { ListItem } from "@mui/material";
import { Slider } from "@mui/material";

const defaultStocks = 50;

class Inputs extends React.Component {

    constructor () {
        super();
        this.state = {
            stockAllocPct: defaultStocks,
            itemSpacing: 3,
        }
    }

    render () {

        return (
            <div className="Inputs">
                <header className="Inputs-header">
                    <Box sx={{
                        bgcolor: 'background.paper',
                        boxShadow: 10,
                        borderRadius: 10,
                        p: 2,
                        minWidth: 300,
                    }} >

                        <List> 
                            <ListItem>
                                <TextField required label="Portfolio Value" defaultValue="1000000" />
                            </ListItem>
                            <ListItem divider >
                                <TextField required label="Annual Spend" defaultValue="40000" />
                            </ListItem>
                            <ListItem >
                                <div >Stocks: </div>
                                <div id="sAlloc" >{this.state.stockAllocPct}%</div>
                                <div >&nbsp; Bonds: </div>
                                <div id="bAlloc" >{100-this.state.stockAllocPct}%</div>
                            </ListItem> 
                            <ListItem>
                                <Slider id="aAlloc" label="Stock Allocation" marks step={5} 
                                valueLabelDisplay="auto" defaultValue={defaultStocks}  onDragEnd={this.handleChange} />
                            </ListItem>
                        </List>                        
                    </Box>

                </header>
            </div>
        );
    }

    handleChange = (newValue) => { 
        this.setState( {stockAllocPct : newValue} )
    }
}

  export default Inputs;
