import * as React from "react";
import XLSX from 'xlsx';
import { readFile } from 'xlsx';

class AnnualReturnData {

    constructor (year, inflation_pct, stock_pct, bond_pct) {
        this._year = year;
        this._inf = inflation_pct;
        this._stock = stock_pct;
        this._bond = bond_pct;
    }
}

const histFile = "./data/hist-returns.xlsx";
const annualreturns = [];

class HistoricalData extends React.Component {

    constructor() {
        super();

    }

    initHistory () {
       return (readFile(histFile));
    }

    render() {
        return (
            <ReadData />
        )
    }

}

export default HistoricalData;

// demo code from 
// https://github.com/SheetJS/sheetjs/blob/master/demos/react/sheetjs.js

function ReadData() {
	const [data, setData] = React.useState([]);
	const [cols, setCols] = React.useState([]);

	const handleFile = (file) => {
		const reader = new FileReader();
		reader.onload = (e) => {
			/* Parse data */
			const ab = e.target.result;
			const wb = XLSX.read(ab, {type:'array'});
			/* Get first worksheet */
			const wsname = wb.SheetNames[0];
			const ws = wb.Sheets[wsname];
			/* Convert array of arrays */
			const data = XLSX.utils.sheet_to_json(ws, {header:1});
			/* Update state */
			setData(data);
			setCols(make_cols(ws['!ref']))
		};
		reader.readAsArrayBuffer(file);
	}

	//const exportFile = () => {
	//	/* convert state to workbook */
	//	const ws = XLSX.utils.aoa_to_sheet(data);
	//	const wb = XLSX.utils.book_new();
	//	XLSX.utils.book_append_sheet(wb, ws, "SheetJS");
	//	/* generate XLSX file and send to client */
	//	XLSX.writeFile(wb, "sheetjs.xlsx")
	//};

	return (
	<DragDropFile handleFile={handleFile}>
		<div className="row"><div className="col-xs-12">
			<DataInput handleFile={handleFile} />
		</div></div>
		<div className="row"><div className="col-xs-12">
			<button disabled={!data.length} className="btn btn-success" onClick={exportFile}>Export</button>
		</div></div>
		<div className="row"><div className="col-xs-12">
			<OutTable data={data} cols={cols} />
		</div></div>
	</DragDropFile>
	);
}

/*
  Simple HTML Table
  usage: <OutTable data={data} cols={cols} />
    data:Array<Array<any> >;
    cols:Array<{name:string, key:number|string}>;
*/
function OutTable({ data, cols }) {
	return (
		<div className="table-responsive">
			<table className="table table-striped">
				<thead>
					<tr>{cols.map((c) => <th key={c.key}>{c.name}</th>)}</tr>
				</thead>
				<tbody>
					{data.map((r,i) => <tr key={i}>
						{cols.map(c => <td key={c.key}>{ r[c.key] }</td>)}
					</tr>)}
				</tbody>
			</table>
		</div>
	);
}

/* generate an array of column objects */
const make_cols = refstr => {
	let o = [], C = XLSX.utils.decode_range(refstr).e.c + 1;
	for(var i = 0; i < C; ++i) o[i] = {name:XLSX.utils.encode_col(i), key:i}
	return o;
};
