import * as React from "react";
import * as d3 from "d3";
import hist from "./data/hist-returns.csv";

// d3 line chart example : https://www.d3-graph-gallery.com/graph/line_basic.html
// example to draw axis : https://wattenberger.com/blog/react-and-d3
// example : https://sharkcoder.com/data-visualization/d3-react

function HistoricalChart () {
    const [activeIndex, setActiveIndex] = React.useState(null);
    const [histData, setData] = React.useState([]);
    const margin = { top: 40, right: 80, bottom: 60, left: 50 },
    width = 960 - margin.left - margin.right,
    height = 600 - margin.top - margin.bottom;

    const getX = (inData, width) => {
        return (d3.scaleLinear()
                    .domain([d3.min(inData, function(d) { return d.year; }),
                             d3.max(inData, function(d) { return d.year; })])
                    .range([ 0, width ]));
    }

    const getY = (inData, height) => {
        return (d3.scaleLinear()
                    .domain([d3.min(inData, function(d) { return d.equity; }),
                             d3.max(inData, function(d) { return d.equity; })])
                    .range([ height, 0 ]));
    }

    const handleMouseMove = (e) => {
        const bisect = d3.bisector((d) => d.date).left,
            x0 = getX.invert(d3.pointer(e, this)[0]),
            index = bisect(histData, x0, 1);
        setActiveIndex(index);
    };

    const handleMouseLeave = () => {
        setActiveIndex(null);
    };

    const setupAxes = (inData, svg, x, y, width, height) => {
        var pctFormat = d3.format("4.2%");
        var yearFormat = d3.format("d");

        var xGen = d3.axisBottom(x)
                    .tickFormat(yearFormat);

        svg.append("g")
            .attr("transform", "translate(0," + height + ")")
            .call(xGen);

        var yGen = d3.axisLeft(y)
                    .tickFormat(pctFormat);

        svg.append("g")
            .call(yGen);
    };

    const setupLines = (inData, svg, x, y) => {
            // Add the equity line
            svg.append("path")
               .datum(inData)
               .attr("fill", "none")
               .attr("stroke", "steelblue")
               .attr("stroke-width", 1.5)
               .attr("d", d3.line()
                    .x(function(d) { return x(d.year) })
                    .y(function(d) { return y(d.equity) })
                );

            // Add the bonds line
            svg.append("path")
               .datum(inData)
               .attr("fill", "none")
               .attr("stroke", "cyan")
               .attr("stroke-width", 1.5)
               .attr("d", d3.line()
                    .x(function(d) { return x(d.year) })
                    .y(function(d) { return y(d.bond) })
                );

            // Add the inflation line
            svg.append("path")
               .datum(inData)
               .attr("fill", "none")
               .attr("stroke", "red")
               .attr("stroke-width", 1.5)
               .style("stroke-dasharray", ("5, 5"))
               .attr("d", d3.line()
                    .x(function(d) { return x(d.year) })
                    .y(function(d) { return y(d.inflation) })
                );        

    }

    React.useEffect(() => {
        var svg = d3.select("#d3target")
                    .append("svg")
                    .attr("width", width + margin.left + margin.right)
                    .attr("height", height + margin.top + margin.bottom)
                    .append("g")
                    .attr("transform",
                        "translate(" + margin.left + "," + margin.top + ")");

        d3.csv(hist,function(d) {
            return { year: d.year, inflation: +d.inflation, equity: +d.equity, bond: +d.bond };
        })
        .then (function(dataset) {
            setData(dataset);

            var x = getX(dataset, width);
            var y = getY(dataset, height);

            setupAxes(dataset, svg, x, y, width, height);
            setupLines(dataset, svg, x, y);
        })

    }, []);

      return (
        <div>
             <div id="d3target" width="1200" height="1200"              >
                <svg
                    viewBox={`0 0 ${width + margin.left + margin.right} 
                                    ${height + margin.top + margin.bottom}`}
                    onMouseMove={handleMouseMove}
                    onMouseLeave={handleMouseLeave}
                >
                </svg>
             </div> 
        </div>
      );
}

export default HistoricalChart;
