import * as React from "react";
import {useState} from "react";
import { Stack } from "@mui/material";
import { Card } from "@mui/material";
import { CardContent } from "@mui/material";
import { Typography } from "@mui/material";
import { getPerRunClassName, getThresholdValues, getColorStringForRelativeValue, findByID, findByClass, getPortfolioLineClassName} from "./common.js";

function SummaryCards (props) {

    React.useEffect(() => {

    }, [props] );

    return (
        <div>
            <Stack direction="row">
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Summary
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {makePct(1 - (props.fails / props.cycles))} success
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {props.fails} failures from {props.cycles} cycles                               
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            Median failure age : {props.medianfailage}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            Minimum failure age : {minFailAgeState}
                        </Typography>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            $ End Value
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : {makeCurrency(medianAdjEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : {makeCurrency(avgAdjEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : {makeCurrency(maxAdjEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : {makeCurrency(minAdjEndValueState)}
                        </Typography>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            % Returns
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : { makePct(medianReturnsState) }
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : { makePct(avgReturnsState) }
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : { makePct(maxReturnsState) }
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : { makePct(minReturnsState) }
                        </Typography>
                    </CardContent>
                </Card>                
            </Stack>             
        </div>
    );    
};

export default SummaryCards;
