import * as React from "react";
import * as d3 from "d3";
import "./chartdata.css";
import hist from "./data/hist-returns.csv";

// d3 line chart example : https://www.d3-graph-gallery.com/graph/line_basic.html
// example to draw axis : https://wattenberger.com/blog/react-and-d3
// example : https://sharkcoder.com/data-visualization/d3-react

function HistoricalChart () {
    const [histDataState, setDataState] = React.useState([]);
    const [loadedState, setLoadedState] = React.useState(false);
    const margin = { top: 40, right: 80, bottom: 60, left: 50 };
    const width = 960 - margin.left - margin.right;
    const height = 600 - margin.top - margin.bottom;
    const equityColor = "OrangeRed";
    var focus = null;

    const getXScale = (dataIn, widthIn) => { 
        var xExt = d3.extent(dataIn, (d) => d.year);
        return d3.scaleLinear()
                 .domain(xExt)
                 .range([ 0, widthIn ]);
    }

    const getYScale = (dataIn, heightIn) => {
        var yExt = d3.extent(dataIn, (d) => d.equity);
        return d3.scaleLinear()
                 .domain(yExt)
                 .range([ heightIn, 0 ]);
    }

    const drawAxes = (svg, xScaleIn, yScaleIn, heightIn) => {
        var pctFormat = d3.format("4.2%");
        var yearFormat = d3.format("d");
        var xGen = d3.axisBottom(xScaleIn)
                     .tickFormat(yearFormat);            
        var yGen = d3.axisLeft(yScaleIn)
                     .tickFormat(pctFormat);    

        svg.append("g")
            .attr("transform", "translate(0," + heightIn + ")")
            .call(xGen);
        svg.append("g")
           .call(yGen);
    };

    const drawLines = (svg, dataIn, xScaleIn, yScaleIn) => {
        // Add the equity line
        svg.append("path")
            .datum(dataIn)
            .attr("fill", "none")
            .attr("stroke", equityColor)
            .attr("stroke-width", 1.5)
            .attr("d", d3.line()
                        .x(function(d) { return xScaleIn(d.year) })
                        .y(function(d) { return yScaleIn(d.equity) })
            );
    }

    const prepHoverStuff = (svg) => {
        // focus indicator
        focus = svg
        .append('g')
        .append('circle')
            .style("fill", "none")
            .attr("stroke", "black")
            .attr('r', 8.5)
            .style("opacity", 0);

        // mouse tracking stuff
        svg
        .append('rect')
        .style("fill", "none")
        .style("pointer-events", "all")
        .attr('width', width)
        .attr('height', height)
        .on('mouseover', handleMouseOver)
        .on('mousemove', handleMouseMove)
        .on('mouseout', handleMouseLeave);   
    }

    const isDataTrackingReady = () => {
        return ((null != focus) && 
                (0 < histDataState.length));
    }

    const handleMouseOver = (e) => {
        if (isDataTrackingReady()) {
            focus.style("opacity", 1);
        }
    }

    const handleMouseMove = (e) => {
        const bisect = d3.bisector((d) => d.year).left;
        var xScaleIn = getXScale(histDataState, width);
        const x0 = xScaleIn.invert(d3.pointer(e, this)[0]);
        var i = bisect(histDataState, x0, 1);
        var selectedData = histDataState[i];

        console.log("mouseMove - loaded : " + loadedState + " x0 : " + x0 + " index : " + i);

        if (isDataTrackingReady()) {            
                focus
                .attr("cx", getXScale(selectedData.year))
                .attr("cy", getYScale(selectedData.equity));
        }
    };

    const handleMouseLeave = () => {
        if (isDataTrackingReady()) {
            focus.style("opacity", 0);
        }
    };

    React.useEffect(() => {

        var svg = d3.select("#d3target");

        console.log("loading ...");
        d3.csv(hist,function(d) {
            return { year: d.year, inflation: +d.inflation, equity: +d.equity, bond: +d.bond };
        })
        .then (function(dataset) {
            console.log("... loaded");
            var xExtIn = d3.extent(dataset, (d) => d.year);
            var yExtIn = d3.extent(dataset, (d) => d.equity);
            var xScaleIn = getXScale(dataset, width);
            var yScaleIn = getYScale(dataset, height);

            // update state 
            setDataState(dataset);
            setXExtState(xExtIn);
            setYExtState(yExtIn);
            setLoadedState(true);

            drawAxes(svg, xScaleIn, yScaleIn, height);
            drawLines(svg, dataset, xScaleIn, yScaleIn); 
            prepHoverStuff(svg);
        })
    }, [setDataState, setXExtState, setYExtState]);

    return (
        <div>
            <svg id="d3target" 
                 width={960 - margin.left - margin.right}
                 height={600 - margin.top - margin.bottom} >
            </svg>
        </div>
    );
}

export default HistoricalChart;
