import * as React from "react";
import { Grid, TextField, Slider } from "@mui/material";

const handleChange = (event, newValue) => {
    setValue(newValue);
  };

class Inputs extends React.Component {

    render () {

        const [value, setValue] = React.useState(50);

        return (
            <div className="Inputs">
                <header className="Inputs-header">


                    <Grid container spacing={2}>
                        <TextField required label="Portfolio Value" defaultValue="1000000" />
                        <TextField required label="Annual Spend" defaultValue="40000" />
                        <Slider aria-label="Stock Allocation" value={value} />
                    </Grid>



                    <table > 
                        <tr>
                            <td>Portfolio value</td>
                            <td><input type="text" value="1000000"></input></td>
                        </tr>
                        <tr>
                            <td>Annual % spend</td>
                            <td><input type="text" value="4.00"></input></td>
                        </tr>
                        <tr>
                            <td><label>Asset Allocation</label></td>
                            <td>
                                <output id="stockalloc" >80</output>
                                <input type="range" id="asset_alloc" name="alloc" min="0" max="100" value="80" onChange="changeAllocValue(this.value)"></input>
                            </td>
                        </tr>
                    </table>
                    <input type="button" id="submitInputs" value="submit" />
                </header>
            </div>
        );
    }
}

  export default Inputs;
