import * as React from "react";
import { Box } from "@mui/system";
import { Checkbox } from "@mui/material";
import { TextField } from "@mui/material";
import { List } from "@mui/material";
import { ListItem } from "@mui/material";
import { Slider } from "@mui/material";
import { FormGroup } from "@mui/material";
import { FormControlLabel } from "@mui/material";
import { FormControl } from "@mui/material";
import { FormLabel } from "@mui/material";
import { RadioGroup } from "@mui/material";
import { Radio } from "@mui/material";

const defaultStocks = 80;
const defaultSSIncome = 39312;

class Inputs extends React.Component {

    constructor () {
        super();
        this.state = {
            stockAllocPct: defaultStocks,
            ssOn: false,
            socialSecurityIncome: 0,
            socialSecurityAge: 67,
            itemSpacing: 3,
        }
    }

    render () {

        const handleAllocChange = (event, newValue) => { 

            this.setState( {stockAllocPct : newValue} );
        }

        const handleSSToggle = (event, newValue) => {
            this.setState( {SSOn: newValue} );
        }

        return (
            <div className="Inputs">
                <header className="Inputs-header">
                    <Box sx={{
                        bgcolor: 'background.paper',
                        boxShadow: 10,
                        borderRadius: 10,
                        p: 2,
                        minWidth: 300,
                    }} >

                        <List> 
                            <ListItem divider >
                                <TextField required label="Age" type="number" defaultValue="56" 
                                    InputLabelProps={{
                                    shrink: true,
                                  }}
                                />
                            </ListItem>
                            <ListItem>
                                <TextField required type="number" label="Portfolio Value $" defaultValue="1000000" />
                            </ListItem>
                            <ListItem divider >
                                <TextField required type="number" label="Annual Spend $" defaultValue="40000" />
                            </ListItem>
                            <ListItem >
                                <div >Stocks: </div>
                                <div id="sAlloc" >{this.state.stockAllocPct}%</div>
                                <div >&nbsp; Bonds: </div>
                                <div id="bAlloc" >{100-this.state.stockAllocPct}%</div>
                            </ListItem> 
                            <ListItem divider >
                                <Slider id="aAlloc" label="Stock Allocation" marks step={5} 
                                 valueLabelDisplay="auto" defaultValue={defaultStocks}  
                                 onChange={handleAllocChange} />
                            </ListItem>
                            <ListItem >
                                <FormGroup>
                                    <FormControlLabel control={<Checkbox />} label="include Social Security income" 
                                        onChange={handleSSToggle}
                                    />
                                </FormGroup>
                            </ListItem>
                            <ListItem>
                                <TextField disabled={!(this.state.ssOn)} type="number" label="Annual SS $" />
                                <FormControl disabled={!(this.state.ssOn)} component="fieldset">
                                    <FormLabel component="legend">Start at age</FormLabel>
                                    <RadioGroup
                                     aria-label="ss-start"
                                     row
                                     defaultValue="a67"
                                     name="radio-buttons-group">
                                        <FormControlLabel value="a62" control={<Radio />} label="62" />
                                        <FormControlLabel value="a67" control={<Radio />} label="67" />
                                        <FormControlLabel value="a70" control={<Radio />} label="70" />
                                    </RadioGroup>
                                </FormControl>
                            </ListItem>
                        </List>                        
                    </Box>

                </header>
            </div>
        );
    }

}

  export default Inputs;
