import { useState } from "react";
import * as React from "react";
import * as d3 from "d3";
import "./chartdata.css";
import hist from "./data/hist-returns.csv";

// d3 line chart example : https://www.d3-graph-gallery.com/graph/line_basic.html
// example to draw axis : https://wattenberger.com/blog/react-and-d3
// example : https://sharkcoder.com/data-visualization/d3-react

function HistoricalChart () {

    const [histDataState, setHistDataState] = useState([]);
    const [focusState, setFocusState] = useState(null);
    const [tooltipState, setTooltipState] = useState(null);
    const [hoverLineState, setHoverLineState] = useState(null);

    const margin = { top: 40, right: 80, bottom: 60, left: 50 };
    const totalWidth = 960;
    const totalHeight = 700;
    const boundedWidth = totalWidth - margin.left - margin.right;
    const boundedHeight = totalHeight - margin.top - margin.bottom;
    const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
    const equityColor = "OrangeRed";
    const bondColor = "SteelBlue"
    const normalStrokeWidth = 1.5;
    const normalLineOp = 0.5;

    const getXScale = (dataIn, widthIn) => { 
        var xExt = d3.extent(dataIn, (d) => d.year);
        return d3.scaleLinear()
                 .domain(xExt)
                 .range([ 0, widthIn ]);
    }

    const getYScale = (dataIn, heightIn) => {
        var yExt = d3.extent(dataIn, (d) => d.equity);
        return d3.scaleLinear()
                 .domain(yExt)
                 .range([ heightIn, 0 ]);
    }

    const drawAxes = (svg, xScaleIn, yScaleIn, heightIn) => {
        var pctFormat = d3.format("4.2%");
        var yearFormat = d3.format("d");
        var xGen = d3.axisBottom(xScaleIn)
                     .tickFormat(yearFormat);            
        var yGen = d3.axisLeft(yScaleIn)
                     .tickFormat(pctFormat);    

        svg.append("g")
            .attr("transform", "translate(0," + heightIn + ")")
            .call(xGen);
        svg.append("g")
           .call(yGen);
    };

    const drawLines = (svg, dataIn, xScaleIn, yScaleIn) => {
        // Add the equity line
        svg.append("path")
            .datum(dataIn)
            .attr("fill", "none")
            .style("opacity", normalLineOp)
            .attr("stroke", equityColor)
            .attr("stroke-width", normalStrokeWidth)
            .attr("d", d3.line()
                        .x(function(d) { return xScaleIn(d.year) })
                        .y(function(d) { return yScaleIn(d.equity) })
            );
        // Add the bonds line
        svg.append("path")
            .datum(dataIn)
            .attr("fill", "none")
            .attr("stroke", bondColor)
            .attr("stroke-width", normalStrokeWidth)
            .attr("d", d3.line()
                        .x(function(d) { return xScaleIn(d.year) })
                        .y(function(d) { return yScaleIn(d.bond) })
            );

    }

    const prepHoverStuff = (svg) => {
        const focus = svg
        .append('g')
        .append('circle')
            .style('fill', 'none')
            .attr('stroke', 'black')
            .attr('r', 5)
            .style('opacity', 0);

        const tooltip = svg
        .append('g')
        .append('text')
            .style('opacity', 0)
            .attr('text-anchor', 'left');

        const hoverLine = svg
                .append("g")
                .append("rect")
                .style('opacity', 0)
                .attr("class", "dotted")
                .attr("stroke-width", "1px")
                .attr("width", ".5px")
                .attr("height", boundedHeight);

        setFocusState(focus);
        setTooltipState(tooltip);
        setHoverLineState(hoverLine);
    }

    const isDataTrackingReady = () => {
        return ( (0 < histDataState.length) &&
                 (null != focusState)
               );
    }

    const handleMouseOver = (e) => {
        if (isDataTrackingReady()) {
            focusState.style('opacity', 1);
            tooltipState.style('opacity', 1);
            hoverLineState.style('opacity', 1)
        }
    }

    const handleMouseMove = (e) => {
        if (isDataTrackingReady()) {  
            const bisect = d3.bisector((d) => d.year).left;
            var xScaleIn = getXScale(histDataState, boundedWidth);
            var yScaleIn = getYScale(histDataState, boundedHeight);

            const coords = d3.pointer(e);
            const x0 = xScaleIn.invert(coords[0]);
            const i = bisect(histDataState, x0, 1);
            const selectedData = histDataState[i];
            const clientX = xScaleIn(selectedData.year);
            const clientY = yScaleIn(selectedData.equity);

            focusState
                .attr('cx', clientX)
                .attr('cy', clientY);
            tooltipState
                .html('year:' + selectedData.year + '  - x0:' + x0)
                .attr('x', clientX)
                .attr('y', clientY);  
            hoverLineState
                .attr('cx', clientX)
                .attr('cy', 0);
        }
    };

    const handleMouseLeave = () => {
        if (isDataTrackingReady()) {
            focusState.style('opacity', 0);
            tooltipState.style('opacity', 0);
            hoverLineState.style('opacity', 0)
        }
    };

    React.useEffect(() => {

        var svg = d3.select("#d3target")
                    .attr("width", totalWidth)
                    .attr("height", totalHeight)
                    .append("g")
                    .attr("transform",
                          "translate(" + margin.left + "," + margin.top + ")");

        d3.csv(hist,function(d) {
            return { year: d.year, inflation: +d.inflation, equity: +d.equity, bond: +d.bond };
        })
        .then (function(dataset) {

            var xScaleIn = getXScale(dataset, boundedWidth);
            var yScaleIn = getYScale(dataset, boundedHeight);

            drawAxes(svg, xScaleIn, yScaleIn, boundedHeight);            
            drawLines(svg, dataset, xScaleIn, yScaleIn); 
            prepHoverStuff(svg);

            setHistDataState(dataset);
        })
    }, [setHistDataState, setFocusState]);

    return (
        <div>
            <svg id="d3target" 
                 width={totalWidth}
                 height={totalHeight} 
            >
                <rect id='trackingRect'
                 style={{ opacity:0 }}
                 width={boundedWidth}
                 height={boundedHeight}
                 transform={marginTranslate}
                 fill='LightGray'
                 onMouseOver={handleMouseOver}
                 onMouseMove={handleMouseMove}
                 onMouseLeave={handleMouseLeave}
                >
                </rect>
            </svg>            
        </div>
    );
}

export default HistoricalChart;
