    const [histDataState, setHistDataState] = useState([]);
    const [focusState, setFocusState] = useState(null);
    const [tooltipWrapperState, setTooltipWrapperState] = useState(null);
    const [yearTooltipState, setYearTooltipState] = useState(null);
    const [equityTooltipState, setEquityTooltipState] = useState(null);
    const [bondTooltipState, setBondTooltipState] = useState(null);
    const [inflationTooltipState, setInflationTooltipState] = useState(null);
    const [tooltipBackgroundState, setTooltipBackgroundState] = useState(null);
    const [hoverLineState, setHoverLineState] = useState(null);

    const margin = { top: 40, right: 80, bottom: 60, left: 50 };
    const totalWidth = 960;
    const totalHeight = 700;
    const boundedWidth = totalWidth - margin.left - margin.right;
    const boundedHeight = totalHeight - margin.top - margin.bottom;
    const tooltipWidth = 75;
    const tooltipHeight = 75;
    const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
    const equityColor = "Blue";
    const bondColor = "Purple";
    const inflationColor = "DarkGrey";
    const normalStrokeWidth = 1.5;
    const normalLineOp = 0.5;
    var pctFormat = d3.format("4.2%");

    const getXScale = (dataIn, widthIn) => { 
        var xExt = d3.extent(dataIn, (d) => d.year);
        return d3.scaleLinear()
                 .domain(xExt)
                 .range([ 0, widthIn ]);
    }

    const getYScale = (dataIn, heightIn) => {
        var yExt = d3.extent(dataIn, (d) => d.equity);
        return d3.scaleLinear()
                 .domain(yExt)
                 .range([ heightIn, 0 ]);
    }

    const drawAxes = (svg, xScaleIn, yScaleIn, heightIn) => {
        var yearFormat = d3.format("d");
        var xGen = d3.axisBottom(xScaleIn)
                     .tickFormat(yearFormat);            
        var yGen = d3.axisLeft(yScaleIn)
                     .tickFormat(pctFormat);    

        svg.append("g")
            .attr("transform", "translate(0," + heightIn + ")")
            .call(xGen);
        svg.append("g")
           .call(yGen);
    };

    const drawLines = (svg, dataIn, xScaleIn, yScaleIn) => {
        // Add the equity line
        svg.append("path")
            .datum(dataIn)
            .attr("fill", "none")
            .attr("pointer-events", "none")
            .style("opacity", normalLineOp)
            .attr("stroke", equityColor)
            .attr("stroke-width", normalStrokeWidth)
            .attr("d", d3.line()
                        .x(function(d) { return xScaleIn(d.year) })
                        .y(function(d) { return yScaleIn(d.equity) })
            );
        // Add the bonds line
        svg.append("path")
            .datum(dataIn)
            .attr("fill", "none")
            .attr("pointer-events", "none")
            .attr("stroke", bondColor)
            .attr("stroke-width", normalStrokeWidth)
            .attr("d", d3.line()
                        .x(function(d) { return xScaleIn(d.year) })
                        .y(function(d) { return yScaleIn(d.bond) })
            );
        // Add the inflation line
        svg.append("path")
            .datum(dataIn)
            .style("stroke-dasharray", ("3, 3")) 
            .attr("pointer-events", "none")
            .attr("fill", "none")
            .attr("stroke", inflationColor)
            .attr("stroke-width", normalStrokeWidth)
            .attr("d", d3.line()
                        .x(function(d) { return xScaleIn(d.year) })
                        .y(function(d) { return yScaleIn(d.inflation) })
            );

    }

    const prepHoverStuff = (svg) => {
        const focus = svg
                .append('g')
                .append('circle')
                    .style('opacity', 0)
                    .style('fill', 'none')
                    .attr("pointer-events", "none")
                    .attr('stroke', 'black')
                    .attr('r', 5);

        const tooltipWrapper = svg
                .append('g')
                .attr('display', 'none');

        const tooltipBackground = tooltipWrapper.append('rect')
                      .style('opacity', 0.70)
                      .attr('width', tooltipWidth)
                      .attr('height', tooltipHeight)
                      .attr("pointer-events", "none")
                      .attr("fill", "#e8e8e8"); 

        const tooltipText = tooltipWrapper.append('g').append('text');

        tooltipText.attr("pointer-events", "none")
                   .attr('font-weight', 900)
                   .attr('text-anchor', 'left');

        const yearTT = tooltipText
                .append('tspan')
                .attr('x', '5')
                .attr('y', '5')
                .attr('dy', '15px')
                .attr("pointer-events", "none");
        const equityTT = tooltipText
                .append('tspan')
                .attr('x', '5')
                .attr('y', '5')
                .attr('dy', '30px')
                .attr("pointer-events", "none");
        const bondTT = tooltipText
                .append('tspan')
                .attr('x', '5')
                .attr('y', '5')
                .attr('dy', '45px')
                .attr("pointer-events", "none");
        const inflationTT = tooltipText
                .append('tspan')
                .attr('x', '5')
                .attr('y', '5')
                .attr('dy', '60px')
                .attr("pointer-events", "none");                

        const hoverLine = svg
                .append("g")
                .append("rect")
                    .style('opacity', 0)
                    .attr("pointer-events", "none")
                    .attr("class", "dotted")
                    .attr("stroke-width", "1px")
                    .attr("width", ".5px")
                    .attr("height", boundedHeight);

        setFocusState(focus);
        setTooltipWrapperState(tooltipWrapper); 
        setYearTooltipState(yearTT);
        setEquityTooltipState(equityTT);
        setBondTooltipState(bondTT);
        setInflationTooltipState(inflationTT);
        setTooltipBackgroundState(tooltipBackground);
        setHoverLineState(hoverLine);
    }

    const isDataTrackingReady = () => {
        return ( (0 < histDataState.length) &&
                 (null != focusState)
               );
    }

    const handleMouseOver = (e) => {
        if (isDataTrackingReady()) {
            focusState.style('opacity', 1);
            tooltipWrapperState.attr('display', null);
            hoverLineState.style('opacity', 1)
        }
    }

    const handleMouseMove = (e) => {
        if (isDataTrackingReady()) {  
            const bisect = d3.bisector((d) => d.year).left;
            var xScaleIn = getXScale(histDataState, boundedWidth);
            var yScaleIn = getYScale(histDataState, boundedHeight);

            const coords = d3.pointer(e);
            const x0 = xScaleIn.invert(coords[0]);
            const i = bisect(histDataState, x0, 1);
            const selectedData = histDataState[i];
            const clientX = xScaleIn(selectedData.year);
            const clientY = yScaleIn(selectedData.equity);
            var tooltipX = clientX;

            // prevent the tooltip from getting clipped.
            if (boundedWidth <= (clientX + tooltipWidth)) {
                tooltipX = clientX - tooltipWidth;
            }

            focusState
                .attr('cx', clientX)
                .attr('cy', clientY);

            yearTooltipState
                .text(selectedData.year);
            equityTooltipState
                .text('stocks: ' + pctFormat(selectedData.equity));
            bondTooltipState
                .text('bonds: ' + pctFormat(selectedData.bond));
            inflationTooltipState
                .text('inflation: ' + pctFormat(selectedData.inflation));

            const ttBounds = tooltipWrapperState.node().getBBox();
            tooltipBackgroundState                
                .attr('width', ttBounds.width)
                .attr('height', ttBounds.height);

            tooltipWrapperState
                .attr("transform", "translate(" + tooltipX + "," + coords[1] + ")");        
            hoverLineState
                .attr('x', clientX);

            // console.log("invert: " + x0 + " year: " + selectedData.year);
        }
    };

    const handleMouseLeave = () => {
        // BUG - why are we getting mouseleave events while hovering over rect, but not over a line?
        console.log("leave");
        if (isDataTrackingReady()) {
            focusState.style('opacity', 0);
            tooltipWrapperState.attr('display', 'none');
            hoverLineState.style('opacity', 0)
        }
    };

    React.useEffect(() => {

        var svg = d3.select("#d3target")
                    .attr("width", totalWidth)
                    .attr("height", totalHeight)
