import * as React from "react";
import * as d3 from "d3";
import hist from "./data/hist-returns.csv";

// d3 line chart example : https://www.d3-graph-gallery.com/graph/line_basic.html
// example to draw axis : https://wattenberger.com/blog/react-and-d3
function HistoricalChart() {

    React.useEffect(() => {
            // set the dimensions and margins of the graph
        var margin = {top: 50, right: 50, bottom: 50, left: 50},
            width = 800 - margin.left - margin.right,
            height = 600 - margin.top - margin.bottom;

        var svg = d3.select("#d3target")
                    .append("svg")
                    .attr("width", width + margin.left + margin.right)
                    .attr("height", height + margin.top + margin.bottom)
                    .append("g")
                    .attr("transform",
                        "translate(" + margin.left + "," + margin.top + ")");
    /*            
        // Create the scale
        var x = d3.scaleLinear()
            .domain([1921, 2020])     // This is what is written on the Axis: from 0 to 100
            .range([100, 800]);       // This is where the axis is placed: from 100px to 800px

        // Draw the axis
        svg.append("g")
            .attr("transform", "translate(0,50)")      
            .call(d3.axisBottom(x));
    */
        d3.csv(hist,function(d) {
                return { year: +d.year, inflation: +d.inflation, equity: +d.equity, bonds: +d.bonds };
        })
        .then (function(data) {

            var x = d3.scaleLinear()
                .domain([d3.min(data, function(d) { return d.year; }),
                        d3.max(data, function(d) { return d.year; })])
                .range([ 0, width ]);

            svg.append("g")
                .attr("transform", "translate(0," + height + ")")
                .call(d3.axisBottom(x));

            var y = d3.scaleLinear()
                .domain([d3.min(data, function(d) { return d.equity; }),
                            d3.max(data, function(d) { return d.equity; })])
                .range([ height, 0 ]);

            svg.append("g")
                .call(d3.axisLeft(y));

            // Add the equity line
            svg.append("path")
            .datum(data)
            .attr("fill", "none")
            .attr("stroke", "steelblue")
            .attr("stroke-width", 1.5)
            .attr("d", d3.line()
                .x(function(d) { return x(d.year) })
                .y(function(d) { return y(d.equity) })
                );


        })
    })

    return(
        <div>
             <div id="d3target" width="1200" height="1200" >

             </div> 
        </div>
    );
}

