import * as React from "react";
import { Box } from "@mui/system";
import { Accordion, AccordionDetails, AccordionSummary, Checkbox, TextField } from "@mui/material";
import { List } from "@mui/material";
import { ListItem } from "@mui/material";
import { Slider } from "@mui/material";
import { FormGroup } from "@mui/material";
import { FormControlLabel } from "@mui/material";

const defaultStocks = 80;

class Inputs extends React.Component {

    constructor () {
        super();
        this.state = {
            stockAllocPct: defaultStocks,
            itemSpacing: 3,
        }
    }

    render () {

        const handleChange = (event, newValue) => { 

            this.setState( {stockAllocPct : newValue} );
        }

        return (
            <div className="Inputs">
                <header className="Inputs-header">
                    <Box sx={{
                        bgcolor: 'background.paper',
                        boxShadow: 10,
                        borderRadius: 10,
                        p: 2,
                        minWidth: 300,
                    }} >

                        <List> 
                            <ListItem divider >
                                <TextField required label="Age" type="number" defaultValue="56" 
                                    InputLabelProps={{
                                    shrink: true,
                                  }}
                                />
                            </ListItem>
                            <ListItem>
                                <TextField required type="number" label="Portfolio Value $" defaultValue="1000000" />
                            </ListItem>
                            <ListItem divider >
                                <TextField required type="number" label="Annual Spend $" defaultValue="40000" />
                            </ListItem>
                            <ListItem >
                                <div >Stocks: </div>
                                <div id="sAlloc" >{this.state.stockAllocPct}%</div>
                                <div >&nbsp; Bonds: </div>
                                <div id="bAlloc" >{100-this.state.stockAllocPct}%</div>
                            </ListItem> 
                            <ListItem>
                                <Slider id="aAlloc" label="Stock Allocation" marks step={5} 
                                 valueLabelDisplay="auto" defaultValue={defaultStocks}  
                                 onChange={handleChange} />
                            </ListItem>
                            <ListItem >
                                <Accordion>
                                    <AccordionSummary>
                                        <FormGroup>
                                            <FormControlLabel control={<Checkbox />} label="include Social Security income" />
                                        </FormGroup>
                                    </AccordionSummary>
                                    <AccordionDetails>
                                        <TextField type="number" label="Annual SS $" />
                                        <FormControl component="fieldset">
                                        <FormLabel component="legend">Gender</FormLabel>
                                        <RadioGroup
                                            aria-label="gender"
                                            defaultValue="female"
                                            name="radio-buttons-group"
                                        >
                                            <FormControlLabel value="female" control={<Radio />} label="Female" />
                                            <FormControlLabel value="male" control={<Radio />} label="Male" />
                                            <FormControlLabel value="other" control={<Radio />} label="Other" />
                                        </RadioGroup>
                                        </FormControl>

                                    </AccordionDetails>
                                </Accordion>
                            </ListItem>
                        </List>                        
                    </Box>

                </header>
            </div>
        );
    }

}

  export default Inputs;
