class Chart extends React.Component {

    constructor(props) {
        super(props);
    }  

    render () {    
        const svgID = 'd3target';
        const ttWrapID = 'tooltipWrapper';
        const ttBackID = 'tooltipBackground';
        const ttYearID = 'tooltipYear';
        const ttEquityID = 'tooltipEquity';
        const ttBondID = 'tooltipBond';
        const ttInflationID = 'tooltipInflation';
        const hoverLineID = 'hoverLine';

        const margin = { top: 40, right: 80, bottom: 60, left: 50 };
        const totalWidth = 960;
        const totalHeight = 700;
        const boundedWidth = totalWidth - margin.left - margin.right;
        const boundedHeight = totalHeight - margin.top - margin.bottom;
        const tooltipWidth = 75;
        const tooltipHeight = 75;
        const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
        const equityColor = "Blue";
        const bondColor = "Purple";
        const inflationColor = "DarkGrey";
        const normalStrokeWidth = 1.5;
        const normalLineOp = 0.5;
        var pctFormat = d3.format("4.2%");

        const getXScale = (widthIn) => { 
            var xExt = d3.extent(histData, (d) => d.year);
            return d3.scaleLinear()
                     .domain(xExt)
                     .range([ 0, widthIn ]);
        }

        const getYScale = (heightIn) => {
            var yExt = d3.extent(histData, (d) => d.equity);
            return d3.scaleLinear()
                     .domain(yExt)
                     .range([ heightIn, 0 ]);
        }

        const drawAxes = (svg, xScaleIn, yScaleIn, heightIn) => {
            var yearFormat = d3.format("d");
            var xGen = d3.axisBottom(xScaleIn)
                         .tickFormat(yearFormat);            
            var yGen = d3.axisLeft(yScaleIn)
                         .tickFormat(pctFormat);    

            svg.append("g")
                .attr("transform", "translate(0," + heightIn + ")")
                .call(xGen);
            svg.append("g")
               .call(yGen);
        };

        const drawLines = (svg, xScaleIn, yScaleIn) => {
            // Add the equity line
            svg.append("path")
                .datum(histData)
                .attr("fill", "none")
                .attr("pointer-events", "none")
                .style("opacity", normalLineOp)
                .attr("stroke", equityColor)
                .attr("stroke-width", normalStrokeWidth)
                .attr("d", d3.line()
                            .x(function(d) { return xScaleIn(d.year) })
                            .y(function(d) { return yScaleIn(d.equity) })
                );
            // Add the bonds line
            svg.append("path")
                .datum(histData)
                .attr("fill", "none")
                .attr("pointer-events", "none")
                .attr("stroke", bondColor)
                .attr("stroke-width", normalStrokeWidth)
                .attr("d", d3.line()
                            .x(function(d) { return xScaleIn(d.year) })
                            .y(function(d) { return yScaleIn(d.bond) })
                );
            // Add the inflation line
            svg.append("path")
                .datum(histData)
                .style("stroke-dasharray", ("3, 3")) 
                .attr("pointer-events", "none")
                .attr("fill", "none")
                .attr("stroke", inflationColor)
                .attr("stroke-width", normalStrokeWidth)
                .attr("d", d3.line()
                            .x(function(d) { return xScaleIn(d.year) })
                            .y(function(d) { return yScaleIn(d.inflation) })
                );

        }

        const prepHoverStuff = (svg) => {
            const tooltipWrapper = svg
                    .append('g')
                    .attr('id', ttWrapID)
                    .attr('display', 'none');

            const tooltipBackground = tooltipWrapper.append('rect')
                          .style('opacity', 0.70)
                          .attr('id', ttBackID)
                          .attr('width', tooltipWidth)
                          .attr('height', tooltipHeight)
                          .attr("pointer-events", "none")
                          .attr("fill", "#e8e8e8"); 

            const tooltipText = tooltipWrapper.append('g').append('text');

            tooltipText.attr("pointer-events", "none")
                       .attr('font-weight', 900)
                       .attr('text-anchor', 'left');

            const yearTT = tooltipText
                    .append('tspan')
                    .attr('id', ttYearID)
                    .attr('x', '5')
                    .attr('y', '5')
                    .attr('dy', '15px')
                    .attr("pointer-events", "none");
            const equityTT = tooltipText
                    .append('tspan')
                    .attr('id', ttEquityID)
                    .attr('x', '5')
                    .attr('y', '5')
                    .attr('dy', '30px')
                    .attr("pointer-events", "none");
            const bondTT = tooltipText
                    .append('tspan')
                    .attr('id', ttBondID)
                    .attr('x', '5')
                    .attr('y', '5')
                    .attr('dy', '45px')
                    .attr("pointer-events", "none");
            const inflationTT = tooltipText
                    .append('tspan')
                    .attr('id', ttInflationID)
                    .attr('x', '5')
                    .attr('y', '5')
                    .attr('dy', '60px')
                    .attr("pointer-events", "none");                

            const hoverLine = svg
                    .append("g")
                    .append("rect")
                        .style('opacity', 0)
                        .attr('id', hoverLineID)
                        .attr("pointer-events", "none")
                        .attr("class", "dotted")
                        .attr("stroke-width", "1px")
                        .attr("width", ".5px")
                        .attr("height", boundedHeight);

        }

        const getTooltipWrapper = () => {
            var ttWrap = d3.select('#' + ttWrapID);
            return ttWrap;
        }

        const getTooltipBackground = () => {
            return d3.select('#' + ttBackID);
        }

        const getTooltipYear = () => {
            return d3.select('#' + ttYearID);
        }

        const getTooltipEquity = () => {
            return d3.select('#' + ttEquityID);
        }

        const getTooltipBond = () => {
            return d3.select('#' + ttBondID);
        }

        const getTooltipInflation = () => {
            return d3.select('#' + ttInflationID);
        }

        const getHoverLine = () => {
            return d3.select('#' + hoverLineID);
        }

        const handleMouseOver = (e) => {
            getTooltipWrapper().attr('display', null);
            getHoverLine().style('opacity', 1)
        }

        const handleMouseMove = (e) => {
            const bisect = d3.bisector((d) => d.year).left;
            var xScaleIn = getXScale(boundedWidth);
            const coords = d3.pointer(e);
            const x0 = xScaleIn.invert(coords[0]);
            const i = bisect(histData, x0, 1);
            const selectedData = histData[i];
            const clientX = xScaleIn(selectedData.year);
            var tooltipX = clientX;

            // prevent the tooltip from getting clipped.
            if (boundedWidth <= (clientX + tooltipWidth)) {
                tooltipX = clientX - tooltipWidth;
            }

            var ttYear = getTooltipYear();
            ttYear.text(selectedData.year);
            getTooltipEquity().text('stocks: ' + pctFormat(selectedData.equity));
            getTooltipBond().text('bonds: ' + pctFormat(selectedData.bond));
            getTooltipInflation().text('inflation: ' + pctFormat(selectedData.inflation));

            const ttBounds = getTooltipWrapper().node().getBBox();
            getTooltipBackground()                
                .attr('width', ttBounds.width)
                .attr('height', ttBounds.height);

            getTooltipWrapper().attr("transform", "translate(" + tooltipX + "," + coords[1] + ")");        
            getHoverLine().attr('x', clientX);
        };

        const handleMouseLeave = () => {
            console.log("leave");
            getTooltipWrapper().attr('display', 'none');
            getHoverLine().style('opacity', 0)
        };

        var svg = d3.select("#" + svgID)
                    .attr("width", totalWidth)
                    .attr("height", totalHeight)
                    .append("g")
                    .attr("transform",
                            "translate(" + margin.left + "," + margin.top + ")");
        var xScaleIn = getXScale(boundedWidth);
        var yScaleIn = getYScale(boundedHeight);

        drawAxes(svg, xScaleIn, yScaleIn, boundedHeight);            
        // drawLines(svg, xScaleIn, yScaleIn); 
        // prepHoverStuff(svg);
        console.log('render');

        return (
            <div>
                <svg id="d3target" 
                    width={totalWidth}
                    height={totalHeight} 
                >
                    <rect id='trackingRect'
                        style={{ opacity:0.25 }}
                        width={boundedWidth}
                        height={boundedHeight}
                        transform={marginTranslate}
                        fill='LightGray'
                        onMouseEnter={handleMouseOver}
                        onMouseMove={handleMouseMove}
                        onMouseLeave={handleMouseLeave}
                    >
                    </rect>
                </svg>   
            </div>
        );
    };
};

export default Chart;

