import * as React from "react";
import { Box } from "@mui/system";
import { TextField } from "@mui/material";
import { Accordion } from "@mui/material";
import { AccordionSummary } from "@mui/material";
import { AccordionDetails } from "@mui/material";
import { List } from "@mui/material";
import { ListItem } from "@mui/material";
import { Slider } from "@mui/material";
import { Typography } from "@mui/material";

class Inputs extends React.Component {

    constructor () {
        super();
        this.state = {
            itemSpacing: 3,
        }
    }

    render () {

        return (
            <div className="Inputs">
                <header className="Inputs-header">
                    <Box sx={{
                        bgcolor: 'background.paper',
                        boxShadow: 10,
                        borderRadius: 10,
                        p: 2,
                        minWidth: 300,
                    }} >

                        <List> 
                            <ListItem>
                                <TextField required label="Portfolio Value" defaultValue="1000000" />
                            </ListItem>
                            <ListItem divider >
                                <TextField required label="Annual Spend" defaultValue="40000" />
                            </ListItem>
                            <ListItem > 
                                <Accordion>
                                    <AccordionSummary id="allocationSummary" >
                                        <div >Stocks: </div>
                                        <div id="sAlloc" >80%</div>
                                        <div >&nbsp; Bonds: </div>
                                        <div id="bAlloc" >20%</div>
                                    </AccordionSummary>
                                    <AccordionDetails>
                                        <Slider id="aAlloc" label="Stock Allocation" marks step={5} 
                                        valueLabelDisplay="auto" defaultValue={80}  onDragStop={this.handleDragStop} />
                                    </AccordionDetails>
                                </Accordion>
                            </ListItem>
                        </List>                        
                    </Box>

                </header>
            </div>
        );
    }

    // handleDragStop = () => this.props.update(this.value); sAlloc.innerHTML=this.value;
    handleDragStop = () => sAlloc.innerHTML=this.value;
}

  export default Inputs;
