import * as React from "react";
import * as d3 from "d3";
import "./chartdata.css";
import hist from "./data/hist-returns.csv";

// d3 line chart example : https://www.d3-graph-gallery.com/graph/line_basic.html
// example to draw axis : https://wattenberger.com/blog/react-and-d3
// example : https://sharkcoder.com/data-visualization/d3-react

function HistoricalChart () {
    const [activeIndex, setActiveIndex] = React.useState(null);
    const [histData, setData] = React.useState([]);

    const getX = (dataIn, widthIn) => { 
        var xExt = d3.extent(dataIn, (d) => d.year);
        return d3.scaleLinear()
                 .domain(xExt)
                 .range([ 0, widthIn ]);
    }

    const getY = (dataIn, heightIn) => {
        var yExt = d3.extent(dataIn, (d) => d.equity);
        return d3.scaleLinear()
                 .domain(yExt)
                 .range([ heightIn, 0 ]);
    }

    const drawAxes = (svg, x, y, heightIn) => {
        var pctFormat = d3.format("4.2%");
        var yearFormat = d3.format("d");
        var xGen = d3.axisBottom(x)
                     .tickFormat(yearFormat);            
        var yGen = d3.axisLeft(y)
                     .tickFormat(pctFormat);    

        svg.append("g")
            .attr("transform", "translate(0," + heightIn + ")")
            .call(xGen);
        svg.append("g")
           .call(yGen);
    };

    const drawLines = (svg, dataIn, x, y) => {
        const equityColor = "OrangeRed";
        // Add the equity line
        svg.append("path")
            .datum(dataIn)
            .attr("fill", "none")
            .attr("stroke", equityColor)
            .attr("stroke-width", 1.5)
            .attr("d", d3.line()
                        .x(function(d) { return x(d.year) })
                        .y(function(d) { return y(d.equity) })
            );
    }

    const handleMouseMove = (e) => {
        const bisect = d3.bisector((d) => d.year).left,
            x0 = getX.invert(d3.pointer(e, this)[0]),
            index = bisect(histData, x0, 1);
        setActiveIndex(index);
    };

    const handleMouseLeave = () => {
        setActiveIndex(null);
    };


    React.useEffect(() => {
        const margin = { top: 40, right: 80, bottom: 60, left: 50 },
              width = 960 - margin.left - margin.right,
              height = 600 - margin.top - margin.bottom;

        var svg = d3.select("#d3target")
                    .append("svg")
                    .attr("width", width + margin.left + margin.right)
                    .attr("height", height + margin.top + margin.bottom)
                    .append("g")
                    .attr("transform",
                          "translate(" + margin.left + "," + margin.top + ")");

        d3.csv(hist,function(d) {
            return { year: d.year, inflation: +d.inflation, equity: +d.equity, bond: +d.bond };
        })
        .then (function(dataset) {
            setData(dataset);
            var x = getX(dataset, width);
            var y = getY(dataset, height);

            // xy axes
            drawAxes(svg, x, y, height);

            // chart lines
            drawLines(svg, dataset, x, y);          
        })
    }, []);

    return (
        <div>
            <svg id="d3target">
            </svg>
        </div>
    );
}

export default HistoricalChart;
