    const showSummaryData = () => {
        var ext = d3.extent(allCycleMeta, (d) => d.endCycleValue);
        var avg = d3.mean(allCycleMeta, (d) => d.endCycleValue);
        var med = d3.median(allCycleMeta, (d) => d.endCycleValue);
        var medianSpend = d3.median(allCycleMeta, (d) => d.totalSpend);
        var medianReturns = d3.median(allCycleMeta, (d) => d.totalReturn);
        var numFails = 0;

        for (var i = 0; i < allCycleMeta.length; i++) {
            if (allCycleMeta[i].extent[0] <= 0) {
                numFails++;
            }
        }

        setMinEndValueState(ext[0]);
        setMaxEndValueState(ext[1]);
        setAvgEndValueState(avg);
        setMedianEndValueState(med);
        setAvgSpendState(props.spendvalue);
        setMedianSpendState(props.spendvalue);
        setMinSpendState(props.spendvalue);
        setMaxSpendState(props.spendvalue);
        setNumCyclesState(allCycleMeta.length);
        setNumFailsState(numFails);
    }

    const calcCycles = (svg) => {
        const lifetime = props.lifeexpectancy - props.currentage;
        const numCycles = (histData.length - lifetime);
        const startIndex = 0;
        var portMin = Number.POSITIVE_INFINITY;
        var portMax = Number.NEGATIVE_INFINITY;

        cleanupPrev();

        for (var i = 0; i < numCycles; i++) {
            var oneCycle = runCycle(startIndex + i, lifetime);
            var cycleMeta = calcCycleMeta(oneCycle);

            allCycleMeta[i] = cycleMeta;
            allCycleData[i] = oneCycle;

            portMin = Math.min(portMin, cycleMeta.extent[0]);
            portMax = Math.max(portMax, cycleMeta.extent[1]);
        }

        showSummaryData(allCycleMeta);

        const xScale = getXScale();
        const yScale = getYScale(portMin, portMax);

        drawAxes(svg, xScale, yScale);
        for (i = 0; i < numCycles; i++) {
            drawPortfolioLine(svg, xScale, yScale, allCycleMeta[i], allCycleData[i]);
        }
    }

    React.useEffect(() => {

        const svgSelect = '#' + svgID;
        const svg = d3.select(svgSelect)
                      .append("g")
                      .attr("transform", marginTranslate);
        calcCycles(svg);

        // prepHoverStuff(svg);
        //console.log('effect');
    }, [props] );

    //console.log('render');
    return (
        <div>
            <Stack direction="row">
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Summary
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {Number( (1 - (numFailsState / numCyclesState)) * 100).toFixed(2)}% success
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {numFailsState} failure of {numCyclesState} cycles                               
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min age to failure
                            median age to failure
                        </Typography>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Portfolio
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : {makeCurrency(medianEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : {makeCurrency(avgEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : {makeCurrency(maxEndValueState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : {makeCurrency(minEndValueState)}
                        </Typography>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent>
                        <Typography gutterBottom variant="h6" component="h2">
                            Spending
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            median : {makeCurrency(medianSpendState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            mean : {makeCurrency(avgSpendState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            max : {makeCurrency(maxSpendState)}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            min : {makeCurrency(minSpendState)}
                        </Typography>
                    </CardContent>
                </Card>
            </Stack>
            <svg id={svgID} 
                    width={totalWidth}
                    height={totalHeight} 
            >
                <rect id='trackingRect'
                    style={{ opacity:0 }}
                    width={boundedWidth}
                    height={boundedHeight}
                    transform={marginTranslate}
                    fill='LightGrey'
                    onMouseEnter={handleMouseOver}
                    onMouseMove={handleMouseMove}
                    onMouseLeave={handleMouseLeave}
                />
            </svg>   
        </div>
    );
};

