import * as React from "react";
import * as d3 from "d3";
import './common.js';
import { getHighValueColorString, getLowValueColorString, getThresholdValues } from "./common.js";

function EndValueChart (props) {

    const svgID = 'endvaluechartsvg';
    const svgSelect = '#' + svgID;
    const perRunClass = 'perrunevdata';
    const histoBarClass = 'histobar';
    const areaClass = 'areaclass';
    const barGroupID = 'bargroup';
    const pathGroupID = 'pathgroupid';
    const margin = { top: 40, right: 65, bottom: 40, left: 65 };
    const totalWidth = 960;
    const totalHeight = 300;
    const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
    const boundedWidth = totalWidth - margin.left - margin.right;
    const boundedHeight = totalHeight - margin.top - margin.bottom;
    const ttBinWrapID = 'ttevwrap';
    const ttBinBackID = 'ttevback';
    const ttBinRangeID = 'ttevrange';
    const ttBinPctID = 'ttevpct';
    const tooltipWidth = 75;
    const tooltipHeight = 75;

    const cleanupPrev = (svg) => {
        const perRunSelect = '.' + perRunClass;
        d3.selectAll(perRunSelect).remove();
    }

    const getXScaleForAxis = () => {
        const thresholdValues = getCurrencyThresholds();
        const numThresholds = thresholdValues.length;
        var thresholdRange = [];
        var thresholdInc = boundedWidth / (numThresholds + 1);

        for (var i = 0; i <= thresholdValues.length; i++) {
            thresholdRange[i] = i * thresholdInc;
        }

        return d3.scaleThreshold()
                 .domain(thresholdValues)
                 .range(thresholdRange);
    }

    const calcBinMetadata = (data) => {
        const oneBinMetadata = {
            'binData': data,
            'extBin': d3.extent(data, (d) => d.adjEndCycleValue),
            'extPctStartValue': d3.extent(data, (d) => d.pctOfStart),
            'binCount': data.length,
        }

        return oneBinMetadata;
    }

    const drawReferenceRects = (bins, parent, xScaleBins) => {
        const lowValueColorString = getLowValueColorString();
        const highValueColorString = getHighValueColorString();
        const refRectOpacity = '0.25';
        const xForOne = xScaleBins(1.0);
        const width = ((xScaleBins(bins[0].x1) - xScaleBins(bins[0].x0)));

        // startValue reference rect
        parent.append('rect')
              .attr('class', perRunClass)
              .style('opacity', refRectOpacity)
              .attr('x', xForOne)
              .attr('y', 0)
              .attr('width', width)
              .attr('height', boundedHeight);
        // failures and 
        // low end value reference rect
        parent.append('rect')
              .attr('class', perRunClass)
              .style('opacity', refRectOpacity)
              .attr('fill', lowValueColorString)
              .attr('x', 0)
              .attr('y', 0)
              .attr('width', xForOne)
              .attr('height', boundedHeight);
        // high value reference rect
        parent.append('rect')
              .attr('class', perRunClass)
              .style('opacity', refRectOpacity)
              .attr('fill', highValueColorString)
              .attr('x', xForOne + width)
              .attr('y', 0)
              .attr('width', boundedWidth - xForOne - width)
              .attr('height', boundedHeight);
    }

    const drawReferenceLines = (bins, parent, xScaleBins) => {

        const xCenterIncrement = ((xScaleBins(bins[0].x1) - xScaleBins(bins[0].x0)) / 2)
        const xForOne = xScaleBins(1.0) + xCenterIncrement;
        const textMargin = 15;

        parent.append('line')
              .attr('class', perRunClass)
              .style('stroke', 'black')
              .style('stroke-width', '1.5')
              .style("stroke-dasharray", ("3, 3, 5")) 
              .attr('x1', xForOne)
              .attr('y1', boundedHeight)
              .attr('x2', xForOne)
              .attr('y2', 0);
        parent.append('text')
              .attr('class', perRunClass)
              .attr('x', xForOne + textMargin)
              .attr('y', textMargin)
              .text('start value');

        const medianPct = props.medianendvalue / props.startvalue;
        const xMedian = xScaleBins(medianPct) + xCenterIncrement;

        parent.append('line')
              .attr('class', perRunClass)
              .style('stroke', 'black')
              .style('stroke-width', '1.5')
              .style("stroke-dasharray", ("5, 5, 5, 3")) 
              .attr('x1', xMedian)
              .attr('y1', boundedHeight)
              .attr('x2', xMedian)
              .attr('y2', 0);
        parent.append('text')
              .attr('class', perRunClass)
              .attr('x', xMedian + textMargin)
              .attr('y', textMargin * 2)
              .text('median end value');
    }

    const getCurrencyThresholds = () => {
        const pctThresholds = getThresholdValues();
        const currencyThresholds = [];

        // create failure bin
        currencyThresholds[0] = 0;
        currencyThresholds[1] = 0.01;
        // start at index 1, as we've handled the zero case.  
        // Consider removing zero from the common thresholds.
        // for now, offset the first entry in the currency array to accomodate the failure bin.
        for (var i = 1; i <= pctThresholds.length; i++) {
            currencyThresholds[i+1] = pctThresholds[i] * props.startvalue;
        }
        return currencyThresholds;
    }

    const createBins = (currencyThresholdValues) => {

        const binFunc = d3.bin()
                          .value(function(d) { return d.pctOfStart; })   
                          .domain([currencyThresholdValues[0], 
                                   currencyThresholdValues[currencyThresholdValues.length - 1]])
                          .thresholds(currencyThresholdValues); 

        return binFunc(props.metadata);
    }

    const drawAreaChart = (svg) => {
        const currencyThresholdValues = getCurrencyThresholds();
        const bins = createBins();
        const xScale = getXScaleForAxis(currencyThresholdValues);
        const yScale = d3.scaleLinear()
                         .domain([0, d3.max(bins, function(d) { return d.length; })])
                         .range([boundedHeight, 0]);

        svg.append("g")
            .attr("class", perRunClass)
            .attr("transform", "translate(0," + boundedHeight + ")")
            .call(d3.axisBottom(xScale));
        svg.append("g")
            .attr("class", perRunClass)
            .call(d3.axisLeft(yScale));

        // draw the histogram bars 
        var pathGroup = svg.append('g')
                           .attr('class', perRunClass)
                           .attr('id', pathGroupID);

        drawReferenceRects(bins, pathGroup, xScale);   
        drawReferenceLines(bins, pathGroup, xScale); 

        pathGroup.append("path")
                    .datum(bins)
                    .style('fill-opacity', '0.1')
                    .attr('class', perRunClass)
                    .attr("fill", "Grey")
                    .attr("stroke", 'Blue')
                    .attr("stroke-width", 1.5)
                    .attr("d", d3.area()
                        .x(d => xScale(d.x0 + ((d.x1-d.x0) / 2)))
                        .y0(yScale(0))
                        .y1(d => yScale(d.length))
                        .curve(d3.curveBasis)
                );          
    }

    const drawHistogram = (svg) => {

        const bins = createBins();
        const xScaleBins = getXScaleForAxis();
        const yScaleBins = d3.scaleLinear()
                             .domain([0, d3.max(bins, function(d) { return d.length; })])
                             .range([boundedHeight, 0]);

        svg.append("g")
            .attr("class", perRunClass)
            .attr("transform", "translate(0," + boundedHeight + ")")
            .call(d3.axisBottom(xScaleBins));
        svg.append("g")
           .attr("class", perRunClass)
           .call(d3.axisLeft(yScaleBins));

        // draw the histogram bars 
        var barGroup = svg.append('g')
                          .attr('class', perRunClass)
                          .attr('id', barGroupID);

        // draw the reference rects (startValue, reds/greens)
        drawReferenceRects(bins, barGroup, xScaleBins);

        var bars = barGroup.selectAll('.' + histoBarClass);
        bars.data(bins)
           .enter()
           .append("rect")
                .attr("class", perRunClass)
                .attr("x", 1)
                .attr("transform", function(d) { return "translate(" + xScaleBins(d.x0) + "," + yScaleBins(d.length) + ")"; })
                .attr("width", function(d) { return xScaleBins(d.x1) - xScaleBins(d.x0) -1 ; })
                .attr("height", function(d) { return boundedHeight - yScaleBins(d.length); })
                .style("fill", "SteelBlue")
                .on("mouseover", handleMouseOver)
                .on("mouseout", handleMouseLeave)                
                .on('mousemove', handleMouseMove);
    }

    const prepTooltip = (svg) => {
        // wrapper
        // background
        // text element
        //  tspan for each line
        //  x0, x1, % of start, % of results

        const tooltipWrapper = svg.append('g')
                                  .attr('id', ttBinWrapID)
                                  .attr("class", perRunClass)
                                  .style('opacity', 0);
        tooltipWrapper.append('rect')
                      .style('opacity', 0.70)
                      .attr('id', ttBinBackID)
                      .attr('width', tooltipWidth)
                      .attr('height', tooltipHeight)
                      .attr("pointer-events", "none")
                      .attr("fill", "#e8e8e8"); 

        const tooltipText = tooltipWrapper.append('g').append('text')
                                          .attr("pointer-events", "none")
                                          .attr('font-weight', 900)
                                          .attr('text-anchor', 'left');
        tooltipText.append('tspan')
                   .attr('id', ttBinRangeID)
                   .attr('x', '5')
                   .attr('y', '5')
                   .attr('dy', '15px')
                   .attr("pointer-events", "none");
        tooltipText.append('tspan')
                   .attr('id', ttBinPctID)
                   .attr('x', '5')
                   .attr('y', '5')
                   .attr('dy', '30px')
                   .attr("pointer-events", "none");
    }

    const makeCurrency = (num) => {
        let dollarUSLocale = Intl.NumberFormat('en-US', {
            style: "currency",
            currency: "USD",
        });
        return dollarUSLocale.format(num);
    }

    const makePct = (num) => {
        return Number( num * 100).toFixed(2) + '%';
    }

    const getTooltip = () => {
        return d3.select('#' + ttBinWrapID);
    }

    const getTooltipBackground = () => {
        return d3.select('#' + ttBinBackID);
    }

    const getBinRangeSpan = () => {
        return d3.select('#' + ttBinRangeID);
    }

    const getBinPctSpan = () => {
        return d3.select('#' + ttBinPctID);
    }

    const handleMouseOver = (e) => {
        getTooltip().style('opacity', 1);
    }

    const handleMouseMove = (e) => {
        const binData = e.srcElement.__data__;
        const binMeta = calcBinMetadata(binData);

        // set the tt content
        const tt = getTooltip();

        const rangeString = makeCurrency(+(binMeta.extBin[0])) + 
                            '-' + makeCurrency(+(binMeta.extBin[1]));
        const binSizeString = ' ' + makePct(binData.length / props.metadata.length) +
                              ' of cycles';
        getBinRangeSpan().text(rangeString);
        getBinPctSpan().text(binSizeString);

        // place the tt
        const ttBounds = tt.node().getBBox();
        getTooltipBackground()                
            .attr('width', ttBounds.width)
            .attr('height', ttBounds.height);
    };

    const handleMouseLeave = () => {
        getTooltip().style('opacity', 0);
    };

    React.useEffect(() => {

        const svg = d3.select(svgSelect)
                      .append("g")
                      .attr("transform", marginTranslate);

        cleanupPrev(svg);
        // drawHistogram(svg);
        drawAreaChart(svg);
        prepTooltip(svg);
    }, [props] );

    return (
        <div>
            <svg id={svgID}  
                width={totalWidth}
                height={totalHeight} 
             >
            </svg>
        </div>
    );
};

export default EndValueChart;
