import * as React from "react";
import { useState } from "react";
import * as d3 from "d3";

function EndValueChart (props) {

    const [binMetadataState, setBinMetadataState] = useState([]);

    const svgID = 'endvaluechartsvg';
    const perRunClass = 'perrunevdata';
    const margin = { top: 40, right: 65, bottom: 40, left: 65 };
    const totalWidth = 960;
    const totalHeight = 300;
    const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
    const boundedWidth = totalWidth - margin.left - margin.right;
    const boundedHeight = totalHeight - margin.top - margin.bottom;
    const ttBinttBinWrapID = 'ttwrap';
    const ttColor = 'white';
    const ttBackColor = 'black';
    const tooltipWidth = 75;
    const tooltipHeight = 75;
    const thresholdValues = [0, 0.25, 0.5, 1, 3, 5, 10, 50];

    const cleanupPrev = (svg) => {
        const perRunSelect = '.' + perRunClass;
        d3.selectAll(perRunSelect).remove();
    }

    const getXScaleForAxis = () => {
        const numThresholds = thresholdValues.length;
        var thresholdRange = [];
        var thresholdInc = boundedWidth / (numThresholds + 1);

        for (var i = 0; i <= thresholdValues.length; i++) {
            thresholdRange[i] = i * thresholdInc;
        }

        return d3.scaleThreshold()
                 .domain(thresholdValues)
                 .range(thresholdRange);
    }

    const calcBinMetadata = (data) => {
        const oneBinMetadata = {
            'binData': data,
            'extBin': d3.extent(data, (d) => d.adjEndCycleValue),
            'extPctStartValue': d3.extent(data, (d) => d.pctOfStart),
            'binCount': data.length,
        }

        return oneBinMetadata;
    }

    const prepTooltip = (svg) => {
        // wrapper
        // background
        // text element
        //  tspan for each line
        //  x0, x1, % of start, % of results

        const tooltipWrapper = svg.append('g')
                                  .attr('id', ttBinWrapID)
                                  .attr('display', 'none');


}

    const drawHistogram = (svg) => {
        cleanupPrev(svg);

        const xScaleBins = getXScaleForAxis();
        const binFunc = d3.bin()
                          .value(function(d) { return d.pctOfStart; })   
                          .domain([thresholdValues[0], thresholdValues[thresholdValues.length - 1]])
                          .thresholds(thresholdValues); 
        const bins = binFunc(props.metadata);
        const yScaleBins = d3.scaleLinear()
                             .domain([0, d3.max(bins, function(d) { return d.length; })])
                             .range([boundedHeight, 0]);

        var binsMeta = [];
        for (var i = 0; i < bins.length; i++) {
            binsMeta[i] = calcBinMetadata(bins[i]);
        }

        setBinMetadataState(binsMeta);

        svg.append("g")
            .attr("class", perRunClass)
            .attr("transform", "translate(0," + boundedHeight + ")")
            .call(d3.axisBottom(xScaleBins));
        svg.append("g")
           .attr("class", perRunClass)
           .call(d3.axisLeft(yScaleBins));

           // draw the histogram bars 
        svg.selectAll("rect")
           .data(bins)
           .enter()
           .append("rect")
                .attr("class", perRunClass)
                .attr("x", 1)
                .attr("transform", function(d) { return "translate(" + xScaleBins(d.x0) + "," + yScaleBins(d.length) + ")"; })
                .attr("width", function(d) { return xScaleBins(d.x1) - xScaleBins(d.x0) -1 ; })
                .attr("height", function(d) { return boundedHeight - yScaleBins(d.length); })
                .style("fill", "#69b3a2")
                .on("mouseover", handleMouseOver)
                .on("mouseout", handleMouseLeave)                
                .on('mousemove', handleMouseMove);
    }

    const makeCurrency = (num) => {
        let dollarUSLocale = Intl.NumberFormat('en-US', {
            style: "currency",
            currency: "USD",
        });
        return dollarUSLocale.format(num);
    }

    const makePct = (num) => {
        return Number( num * 100).toFixed(2) + '%';
    }

    const getTooltip = () => {
        return d3.select('#' + ttBinDivID);
    }

    const handleMouseOver = (e) => {
        console.log('over');
        getTooltip().style('opacity', 1);
    }

    const handleMouseMove = (e) => {
        const coords = d3.pointer(e);
        const binData = e.srcElement.__data__;
        const binMeta = calcBinMetadata(binData);

        // set the tt content
        const tt = getTooltip();
        const rangeString = makeCurrency(+(binMeta.extBin[0])) + 
                            '-' + makeCurrency(+(binMeta.extBin[1]));
        const binSizeString = '<br> (' + makePct(binData.length / props.metadata.length) +
                              ' of cycles)';
        const ttContent = rangeString + binSizeString;
        tt.attr('text', ttContent);
        // console.log('move : ' + binData[0].adjEndCycleValue + ' ' + ttContent);

        // place the tt
        tt.style('left', coords[0] + 'px')   
          .style('top', coords[1] + 'px');
    };

    const handleMouseLeave = () => {
        console.log('leave');
        getTooltip().style('opacity', 0);
    };

    React.useEffect(() => {

        const svgSelect = '#' + svgID;
        const svg = d3.select(svgSelect)
                      .append("g")
                      .attr("transform", marginTranslate);

        prepTooltip(svg);
        drawHistogram(svg);
    }, [props] );

    return (
        <div id={containerID}>
            <svg id={svgID}  
                width={totalWidth}
                height={totalHeight} 
             >
                <rect
                    style={{ opacity:0 }}
                    width={boundedWidth}
                    height={boundedHeight}
                    transform={marginTranslate}
                    fill='LightGrey'
                 >
                </rect>
            </svg>
        </div>
    );
};

export default EndValueChart;
