import * as React from "react";
import { styled } from "@mui/system";
import { Box } from "@mui/system";
import { Slider } from "@mui/material";
import { TextField } from "@mui/material";
import { Grid } from "@mui/material";
import { Checkbox } from "@mui/material";
import { Stack } from "@mui/material";
import { Typography } from "@mui/material";
import { Link } from "@mui/material";
import MuiAppBar from '@mui/material/AppBar';

function Copyright(props) {
    return (
      <Typography variant="body2" color="text.secondary" align="center" {...props}>
        {'Copyright © '}
        <Link color="inherit" href="https://mui.com/">
          Your Website
        </Link>{' '}
        {new Date().getFullYear()}
        {'.'}
      </Typography>
    );
  }

  const drawerWidth = 240;

  const AppBar = styled(MuiAppBar, {
    shouldForwardProp: (prop) => prop !== 'open',
  })(({ theme, open }) => ({
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
      marginLeft: drawerWidth,
      width: `calc(100% - ${drawerWidth}px)`,
      transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
    }),
  }));

class Inputs extends React.Component {

    constructor () {
        super();
        this.state = {
            itemSpacing: 3,
        }
    }

    render () {

        return (
            <div className="Inputs">
                <header className="Inputs-header">
                    <Box sx={{
                        bgcolor: 'background.paper',
                        boxShadow: 10,
                        borderRadius: 10,
                        p: 2,
                        minWidth: 300,
                    }} >
                        Safe Withdrawal Rate Calculator

                        <Stack direction="row" spacing={this.state.itemSpacing} >
                            <TextField required label="Portfolio Value" defaultValue="1000000" />
                            <TextField required label="Annual Spend" defaultValue="40000" />
                        </Stack>

                        <Grid container spacing={this.state.itemSpacing} >
                            <Grid item xs={12}>
                            </Grid>
                            <Grid item xs={3}>

                            </Grid>
                            <Grid item xs={3}>
                            </Grid>
                            <Grid item xs={6}>
                            </Grid>
                            <Grid item xs={2} > 
                                Asset Allocation
                            </Grid>
                            <Grid item xs={3} >
                                <TextField id="stockAlloc" label="Stocks" defaultValue="80" />
                            </Grid>
                            <Grid item xs={3} >
                                <TextField id="bondAlloc" label="Bonds" defaultValue="20" />
                            </Grid>
                            <Grid item xs={4}>
                                <Slider label="Stock Allocation" marks step={5} valueLabelDisplay="auto" defaultValue={80}  
                                onDragStop={this.handleDragStop} />

                            </Grid>
                            <Grid item xs={2}>
                                <Checkbox label="Social Security" checked />
                            </Grid>
                        </Grid>
                    </Box>

                </header>
            </div>
        );
    }

    handleDragStop = () => this.props.update(this.value);
}

  export default Inputs;
