import * as React from "react";
import * as d3 from "d3";
import {useState} from "react";

function EndValueChart (props) {

    const svgID = 'endvaluechartsvg';
    const perRunClass = 'perrundata';
    const margin = { top: 40, right: 65, bottom: 40, left: 65 };
    const totalWidth = 960;
    const totalHeight = 300;
    const marginTranslate = "translate(" + margin.left + "," + margin.top + ")";
    const boundedWidth = totalWidth - margin.left - margin.right;
    const boundedHeight = totalHeight - margin.top - margin.bottom;

    const getXScale = () => { 
        var xExt = [props.minendvalue, props.maxendvalue];
        return d3.scaleLinear()
                    .domain(xExt)
                    .range([ 0, boundedWidth ]);
    }

    const drawHistogram = (svg, xScale) => {
        // set the parameters for the histogram
        var binFunc = d3.bin()
                        .value(function(d) { return d.adjEndCycleValue; })   // I need to give the vector of value
                        .domain(xScale.domain())  // then the domain of the graphic
                        .thresholds(xScale.ticks(10)); // then the numbers of bins
        var bins = binFunc(props.metadata);

        const xScaleBins = getXScale();

        var yScaleBins = d3.scaleLinear()
                           .range([height, 0]);
        yScaleBins.domain([0, d3.max(bins, function(d) { return d.length; })]);   // d3.hist has to be called before the Y axis obviously

        svg.append("g")
            .attr("class", perRunClass)
            .attr("transform", "translate(0," + boundedHeight + ")")
            .call(d3.axisBottom(xScaleBins));
        svg.append("g")
           .attr("class", perRunClass)
           .call(d3.axisLeft(yScaleBins));


    }

    React.useEffect(() => {

        const svgSelect = '#' + svgID;
        const svg = d3.select(svgSelect)
                      .append("g")
                      .attr("transform", marginTranslate);

        drawHistogram(svg, xScale, yScale, 0, 100);
        // console.log('endvaluechart effect');
    }, [props] );

    return (
        <div>
            <svg id={svgID}  
                width={totalWidth}
                height={totalHeight} 
             >
                <rect
                    width={boundedWidth}
                    height={boundedHeight}
                    transform={marginTranslate}
                    fill='LightGrey'
                 >

                </rect>
            </svg>
        </div>
    );
};

export default EndValueChart;
